int maxSteps = 512;
float maxDepth = 500.;
float minDist = 0.001;
vec3 lightDir = vec3(.8, -.3, .8);
vec3 spherePos = vec3(0);
float hardness = 10.;
float shadow = .2;

float pDF(vec3 p){ // Physics distance function
    return length(p-spherePos)-1.0;
}

float rDF(vec3 p){ // Render distance function
    return min(
        pDF(p), 
        DF(p)
    );
}

vec3 rnorm(vec3 p, float eps){ // Render normal
    return vec3(
        rDF(p + vec3(eps, 0, 0)) - rDF(p - vec3(eps, 0, 0)),
        rDF(p + vec3(0, eps, 0)) - rDF(p - vec3(0, eps, 0)),
        rDF(p + vec3(0, 0, eps)) - rDF(p - vec3(0, 0, eps))
    )/(2.*eps);
}

void mainImage( out vec4 O, in vec2 U ){

    spherePos = texelFetch(iChannel0, ivec2(0), 0).xyz;
    lightDir = normalize(lightDir);

    vec2 r = iResolution.xy;
    vec2 uv = (2.*U - r)/r.y;
    vec2 muv = (iMouse.xy == vec2(0)?vec2(0):(2.*iMouse.xy - r)/r.y - vec2(0, 1))*vec2(1, .5);

    float depth = 0.0;
    vec3 camDir = vec3(-sin(-muv.x)*cos(muv.y), cos(-muv.x)*cos(muv.y), sin(muv.y));
    vec3 o = texelFetch(iChannel0, ivec2(0.5, 2.5), 0).xyz
        + vec3(0, 0, -.5) - camDir*(8.);// + length(texelFetch(iChannel0, ivec2(.5, 1.5), 0).xyz*10.));
        // Cool idea but needs smoothing
    vec3 camRight = normalize(cross(camDir, vec3(0, 0, 1)));
    vec3 camUp = (cross(camRight, camDir));
    vec3 dirV = normalize(camDir*2. + camRight * uv.x + camUp * uv.y);
    vec3 p;

    for (int i = 0; i++ < maxSteps;){

        p = o + dirV * depth;
        float d = rDF(p);

        depth += d;

        if (depth > maxDepth){
            depth = maxDepth;
            break;
        }

        if (d < minDist){
            break;
        }
    }

    vec3 norm = rnorm(p, depth * .001);

    vec3 l;
    float ldepth = minDist;
    float light = 5.;
    float b = 1e20;
    float c = 1e20;
    float ph = 1e20;
    float w = 1./hardness;

    for (int i = 0; i++ < maxSteps;){

        l = p + norm*minDist + lightDir * ldepth;
        float d = rDF(l);
        if (d < b){
            c = b;
            b = d;
        }

        float y = (i == 0)?0.:d*d/(2.0*ph);
        float a = sqrt(d*d-y*y);
        c = min( c, a/(w*max(0.0,ldepth-y)) );
        ph = d;
        ldepth += d;

        if (ldepth > maxDepth){
            ldepth = maxDepth;
            break;
        }

        if (d < minDist){
            c = 0.;
            break;
        }
    }

    O = vec4(.1, .4, .9, 1);
    light *= smoothstep(0., 1., c+shadow);    
    light *= max(0., (dot(norm, lightDir))) / depth;
    O.xyz *= light;    

    O = pow(O, vec4(1./2.2));
}

