#define rot(x) mat2(cos(x + vec4(0, 33, 11, 0)))

float boxDF( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float DF(vec3 p){
    float minDist = 1e20;
    float x = 0.;


    x = length(p-vec3(5, 0, -6)) - 5.;
    update;


    x = max(boxDF(p-vec3(-2.5, 8, 0), vec3(3)), -length(p-vec3(0, 5,2.8))+5.);
    update;


    //x = max(boxDF(p-vec3(-10, 17, -5), vec3(6)), -length(p-vec3(-11, 17, 3))+5.);

    x = max(p.z + 2., -length(p-vec3(-11, 17, 0))+5.);
    update;

    p.y -= 1.;
    p.xy *= rot(1.);
    x = boxDF(p-vec3(0, 1.5, -2.2), vec3(1));
    update;


    p.z += .8;
    p.yz *= rot(.3);
    x = boxDF(p-vec3(0, -1., -2), vec3(1));
    update;



    return minDist;
}



vec3 pnorm(vec3 p, float eps){ // Physics normal
    return vec3(
        DF(p + vec3(eps, 0, 0)) - DF(p - vec3(eps, 0, 0)),
        DF(p + vec3(0, eps, 0)) - DF(p - vec3(0, eps, 0)),
        DF(p + vec3(0, 0, eps)) - DF(p - vec3(0, 0, eps))
    )/(2.*eps);
}

