            ))*0.02;


            P = P+V*.5;

            if (U.y ==  .5) O.xyz = P;
            if (U.y == 1.5) O.xyz = V;
        }
    } 

    if (U == vec2(.5, 2.5)){
        O = mix(texelFetch(iChannel0, ivec2(U), 0), texelFetch(iChannel0, ivec2(0), 0), .1);
    }
}

