void mainImage(out vec4 O, vec2 u)
{
    u /= iResolution.xy;

    vec4 p = vec4(.9,.3,.9,0),
         o = vec4(.9,.6,.2,0),
         g = vec4(.5, 1,.6,0);

    float t = 1. - abs(fract(iTime / 8.) - .5) * 2.,
          a = clamp(t, .5, .75) - .5,
          b = clamp(t, .75, 1.) - .75;

    O = fract(u.x * 45.) > .5 ?
        abs(u.y - .3 - .4 * a) < .1 ?
            t * 4. < u.x ? p : g :
            u.y > .5 && u.y < .5 + .8 * b ? g : p :
        abs(u.y - .7 + .4 * a) > .1 ?
            u.y > .5 - .8 * b && u.y < .5 ? g : o :
            t * 4. < 2. - u.x ? o : g;
}

//11:02:13

#define time iTime*0.15
#define tau 6.2831853

mat2 makem2(in float theta){float c = cos(theta);float s = sin(theta);return mat2(c,-s,s,c);}
float noise( in vec2 x ){return texture(iChannel0, x*.01).x;}

float fbm(in vec2 p)
{	
	float z=2.;
	float rz = 0.;
	vec2 bp = p;
	for (float i= 1.;i < 6.;i++)
	{
		rz+= abs((noise(p)-0.5)*2.)/z;
		z = z*2.;
		p = p*2.;
	}
	return rz;
}

float dualfbm(in vec2 p)
{
    //get two rotated fbm calls and displace the domain
	vec2 p2 = p*.7;
	vec2 basis = vec2(fbm(p2-time*1.6),fbm(p2+time*1.7));
	basis = (basis-.5)*.2;
	p += basis;

	//coloring
	return fbm(p*makem2(time*1.2));
}

float circ(vec2 p) 
{
	float r = length(p);
	r = log(sqrt(r));
	return abs(mod(r*4.,tau)-0.14)*3.+0.0;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//setup system
	vec2 p = fragCoord.xy / iResolution.xy-0.5;
	p.x *= iResolution.x/iResolution.y;
	p*=4.;

    float rz = dualfbm(p);

	//rings
	p /= exp(mod(time*7.,3.14159));
	rz *= pow(abs((0.1-circ(p))),.9);

	//final color
	vec3 col = vec3(.2,0.1,0.4)/rz;
	col=pow(abs(col),vec3(.89));
	fragColor = vec4(col,1.);
}

#define triSize 1.

vec2 triRepeat( in vec2 p ) {

    //we first repeat the space inside hexagons in a grid,
    //then we divide the hexagons into 6 symmetrical triangles
    //(this is the method i found, there might be another one !)

    const vec2 n      = vec2(0.57735026919, 1.73205080757); // 1./sqrt(3.), sqrt(3.)
    const vec2 offset = vec2(0.57735026919, 0.33333333333); // 1./sqrt(3.), 1/3          

    p /= triSize;
    p += offset;

    // hexagonal repetition

    p  = vec2(mod(p.x,n.y), mod(p.y + ceil(p.x*n.x), 2.) - 1.);
    p -= vec2(n.y, min(sign(p.y)*2.,0.) + 1.) * max(sign(p.x*n.y + abs(p.y) - 2.), 0.);

    // radial symmetry (6)

    p.y = abs(p.y);
    vec2 p2 = p + vec2(p.y,-p.x)*n.y;
    p = abs( p - p2 * max(sign(p2.y)*0.5, 0.) );

    //

    p -= offset;
    p *= triSize;

    return p;
}

float getVal( in vec2 p ) {

    //just put this line at the beginning of the sdf function
    p = triRepeat(p);

    p += vec2(cos(iTime),sin(iTime))*0.1;

    //box sdf from iq
    vec2 d = abs(p) - 0.1;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = 1.5*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = 1.5*(2.0*iMouse.xy-iResolution.xy)/iResolution.y;

	float d = getVal(p);

	// coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
    col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 ) {
        d = getVal(m);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}

vec3 pal( float t )
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263, 0.416, 0.557);


    return a + b*cos( 6.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    uv = uv * 1.;

    float d = distance(fract(uv), vec2(0.5));

    vec3 col = pal(length(uv0) - iTime);


    d = sin(d * 8. - iTime) / 8.;
    d=abs(d);

    d = 0.01 / d;
    finalColor += col * d;
    fragColor = vec4(finalColor, 1.0);
}

