// constants
const float PI = 3.141592653589793;
const float PARTICLE_COLL_SIZE = .25;
const float PARTICLE_RENDER_SIZE = .5;
const vec3 BBOX_MIN = vec3(-15, -100, -2.5);
const vec3 BBOX_MAX = vec3( 15,  100,  2.5);
const vec3 BBOX_EXTENTS = BBOX_MAX - BBOX_MIN;
const vec3 BBOX_CENTER = (BBOX_MIN + BBOX_MAX) * .5;
#define R     iResolution.xy

// set to zero for eight-neigbor voronoi search. four-neighbor seems faster enough to me to enable by default
#define FOUR_NBS 1

// PARTICLES

// enum for the data types making up the structure of each particle
#define UL_NEIGHBORS 0U
#define UR_NEIGHBORS 1U
#define LL_NEIGHBORS 2U
#define LR_NEIGHBORS 3U
#define POS 4U
#define VEL 5U
#define NUM_PARTICLE_DATA_TYPES 6U

// returns the location of the particle within the particle buffer corresponding with the input id 
ivec2 fxLocFromID(uint wd, uint id, uint typ)
{
    uint idx = id * NUM_PARTICLE_DATA_TYPES + typ;
    return ivec2(idx % wd, idx / wd);
}

struct fxParticle
{
    uvec4 nbs[4];
    vec3 pos;
    vec3 vel;
};

// get the particle corresponding to the id
#define fxGetParticle(X) fxGetParticleImpl(iChannel0, uint(R.x), X)
fxParticle fxGetParticleImpl(sampler2D smp, uint wd, uint id)
{
    vec4 data0 = texelFetch(smp, fxLocFromID(wd, id, UL_NEIGHBORS), 0);
    vec4 data1 = texelFetch(smp, fxLocFromID(wd, id, UR_NEIGHBORS), 0);
    vec4 data2 = texelFetch(smp, fxLocFromID(wd, id, LL_NEIGHBORS), 0);
    vec4 data3 = texelFetch(smp, fxLocFromID(wd, id, LR_NEIGHBORS), 0);
    vec4 data4 = texelFetch(smp, fxLocFromID(wd, id, POS), 0);
    vec4 data5 = texelFetch(smp, fxLocFromID(wd, id, VEL), 0);

    fxParticle p;
    p.nbs[0] = uvec4(data0);
    p.nbs[1] = uvec4(data1);
    p.nbs[2] = uvec4(data2);
    p.nbs[3] = uvec4(data3);
    p.pos = data4.xyz;
    p.vel = data5.xyz;

    return p;
}

// write the correct channel of the particle
vec4 fxSaveParticle(fxParticle p, uint dataType)
{    
    switch(dataType)
    {
    case UL_NEIGHBORS:
        return vec4(p.nbs[0]);
    case UR_NEIGHBORS:
        return vec4(p.nbs[1]);
    case LL_NEIGHBORS:
        return vec4(p.nbs[2]);
    case LR_NEIGHBORS:
        return vec4(p.nbs[3]);
    case POS:  
        return vec4(p.pos, 0);
    case VEL:  
        return vec4(p.vel, 0);
    }
}

#define fxGetParticleData(X, Y) fxGetParticleDataImpl(iChannel0, uint(R.x), X, Y)
vec4 fxGetParticleDataImpl(sampler2D smp, uint wd, uint id, uint typ)
{
    return texelFetch(smp, fxLocFromID(wd, id, typ), 0);
}

// global variables, initialized via computeMaxParticles
const uint IDEAL_MAX_PARTICLES = 0x3ffeU;
uint g_MaxParticles = IDEAL_MAX_PARTICLES; // how many particles, total

// computes the real number of particles that we can simulate in case our buffer isn't big enough
// must be called in each buffer before g_MaxParticles is valid
#define computeMaxParticles() computeMaxParticlesImpl(R)
void computeMaxParticlesImpl(vec2 r)
{
    g_MaxParticles = min(IDEAL_MAX_PARTICLES, uint(r.x * r.y) / NUM_PARTICLE_DATA_TYPES);
}

// PERSISTENT STATE

struct fxState
{
    vec2 lastMouse;
    vec2 camAngle;
    float camOrbit;
    float res;
    bool normal;
    bool sim;
    bool spec;
    bool depth;
    bool ref;
    bool density;
    bool blur;
    bool eightNbs;
    bool paint;
    bool camMotion;
    bool mercury;
    bool noise;
};

#define fxGetState() fxGetStateImpl(iChannel1) // channel 1 is Buffer B
fxState fxGetStateImpl(sampler2D smp)
{
    vec4 data0 = texelFetch(smp, ivec2(0, 0), 0);
    vec4 data1 = texelFetch(smp, ivec2(1, 0), 0);
    vec4 data2 = texelFetch(smp, ivec2(2, 0), 0);
    vec4 data3 = texelFetch(smp, ivec2(3, 0), 0);
    vec4 data4 = texelFetch(smp, ivec2(4, 0), 0);

    fxState s;
    s.camMotion = data0.x != 0.;
    s.lastMouse = data0.yz;
    s.camAngle = data3.yz;
    s.res = data1.y;
    s.normal = data1.z != 0.;
    s.sim = data2.x != 0.;
    s.spec = data2.y != 0.;
    s.depth = data2.z != 0.;
    s.ref = data2.w != 0.;
    s.density = data3.x != 0.;
    s.blur = data0.w != 0.;
    s.camOrbit = data1.w;
    s.eightNbs = data3.w != 0.;
    s.paint = data1.x != 0.;
    s.mercury = data4.x != 0.;
    s.noise = data4.y != 0.;

    return s;
}

vec4 fxPutState(fxState s, ivec2 ifc)
{
    if (ifc == ivec2(0, 0))
        return vec4(s.camMotion ? 1 : 0, s.lastMouse, s.blur ? 1 : 0);
    else if (ifc == ivec2(1, 0))
        return vec4(s.paint ? 1. : 0., s.res, s.normal ? 1 : 0, s.camOrbit);
    else if (ifc == ivec2(2, 0))
        return vec4(s.sim ? 1 : 0, s.spec ? 1 : 0, s.depth ? 1 : 0, s.ref ? 1 : 0);
    else if (ifc == ivec2(3, 0))
        return vec4(s.density ? 1 : 0, s.camAngle, s.eightNbs ? 1 : 0);
    else
        return vec4(s.mercury ? 1 : 0, s.noise ? 1 : 0, 0, 0);
}

bool isStatePixel(ivec2 ifc)
{
    return ifc.y == 0 && ifc.x >= 0 && ifc.x < 5;
}

// CAMERA

mat2 rotate(float a)
{
    vec2 sc = vec2(sin(a), cos(a));
    return mat2(sc.y, -sc.x, sc.x, sc.y);
}

void fxCalcCamera(fxState s, int resolutionWidth, out vec3 ro, out vec3 fwd, out vec3 lft, out vec3 up)
{
    vec3 ta = vec3(0);

    ro = vec3(0, 1, 1) * s.camOrbit;
    ro.yz *= rotate(s.camAngle.y + .6);
    ro.xz *= rotate(s.camAngle.x * 2.);
    ro += ta;

    fwd = normalize(ta - ro);
    lft = -normalize(cross(fwd, vec3(0.0,1.0,0.0)));
    up = normalize(cross(lft, fwd));
}

mat4 fxCalcCameraMat(vec3 ro, vec3 fwd, vec3 lft, vec3 up)
{
    return mat4(vec4(-.5 * lft, 0),
                vec4(-.5 * up , 0),
                vec4(fwd      , 0),
                vec4(ro       , 1));
}

vec3 fxCalcRay(vec2 u, vec2 r, vec3 fwd, vec3 lft, vec3 up)
{
	vec2 s = (u - .5 * r.xy) / r.y;
	return normalize(fwd - s.x * lft - s.y * up);
}

// G BUFFER

struct fxGBufferPixel
{
    float d; // density
    float t; // distance from camera
    float z; // z depth
    float bz; // blurred z depth
};

vec4 fxSaveGBuffer(fxGBufferPixel pix)
{
    return vec4(pix.d, pix.t, pix.z, pix.bz);
}

#define fxGetGBuffer(X) fxGetGBufferImpl(iChannel2, X, R)
fxGBufferPixel fxGetGBufferImpl(sampler2D smp, vec2 u, vec2 r)
{
    vec4 p = texture(smp, u/r);

    fxGBufferPixel pix;
    pix.d = p.x;
    pix.t = p.y;
    pix.z = p.z;
    pix.bz = p.w;

    return pix;
}

// MISC

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_SPACE 32
#define KEY_UP    38
#define KEY_DOWN  40
#define KEY_8     56
#define KEY_B     66
#define KEY_C     67
#define KEY_D     68
#define KEY_M     77
#define KEY_N     78
#define KEY_O     79
#define KEY_P     80
#define KEY_R     82
#define KEY_S     83
#define KEY_Z     90

uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1./float(0xffffffffU));
}

float length2(vec2 v) { return dot(v, v); }
float length2(vec3 v) { return dot(v, v); }

// https://iquilezles.org/articles/spherefunctions/
vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot(oc, rd);
	float c = dot(oc, oc) - sph.w * sph.w;
	float h = b*b - c;
	if (h<0.) return vec2(-1, -1);
	return vec2(-b - sqrt( h ), -b + sqrt( h ));
}

// VORONOI

#if FOUR_NBS

// returns the ids of the four closest particles from the input
#define fxGetClosest(X) fxGetClosestImpl(iChannel1, X) // channel 1 is Buffer B
#define fxGetClosestLight(X) fxGetClosestImpl(iChannel3, X) // channel 3 is buffer D
uvec4 fxGetClosestImpl(sampler2D smp, ivec2 v)
{
    return uvec4(texelFetch(smp, v, 0));
}

#else // FOUR_NBS

uvec4 pack2(uvec4 a, uvec4 b)
{
    return (a & 0xffffU) | ((b & 0xffffU) << 16);
}

void unpack2(uvec4 p, out uvec4 a, out uvec4 b)
{
    a = p & 0xffffU;
    b = (p & 0xffff0000U) >> 16;
}

// returns the ids of the eight closest particles from the input
#define fxGetClosest(X, Y, Z) fxGetClosestImpl(iChannel1, X, Y, Z) // channel 1 is Buffer B
#define fxGetClosestLight(X, Y, Z) fxGetClosestImpl(iChannel3, X, Y, Z) // channel 3 is buffer D
void fxGetClosestImpl(sampler2D smp, ivec2 v, out uvec4 a, out uvec4 b)
{
    uvec4 x = floatBitsToUint(texelFetch(smp, v, 0));
    unpack2(x, a, b);
}

vec4 fxSaveClosest(uvec4 a, uvec4 b)
{
    return uintBitsToFloat(pack2(a, b));
}

#endif // FOUR_NBS

// SCENE

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

const float THICK = 1.;

float sdScene(vec3 p)
{
    float d = sdBox(p, BBOX_EXTENTS * .5 + THICK);
    float side = abs(mod(p.y, 20.) - 10.) > 5. ? 12.5: -12.5;
    p.y = mod(p.y, 10.0);
    const vec3 CUTOUT_A = vec3(BBOX_EXTENTS.x, 10. - THICK, BBOX_EXTENTS.z + 3.) * .5;
    d = max(d, -sdBox(p - vec3(0, 5., 0), vec3(CUTOUT_A)));
    const vec3 CUTOUT_B = vec3(5., 20, BBOX_EXTENTS.z + 3.) * .5;

    d = max(d, -sdBox(p - vec3(side, 7.5, 0), vec3(CUTOUT_B)));
    return d;
}

float sdPhysicsScene(vec3 p)
{
    float d = sdBox(p, BBOX_EXTENTS + THICK);
    float side = abs(mod(p.y, 20.) - 10.) > 5. ? 12.5: -12.5;
    p.y = mod(p.y, 10.0);
    const vec3 CUTOUT_A = vec3(BBOX_EXTENTS.x, 10. - THICK, BBOX_EXTENTS.z) * .5;
    d = max(d, -sdBox(p - vec3(0, 5., 0), vec3(CUTOUT_A)));
    const vec3 CUTOUT_B = vec3(5., 20, BBOX_EXTENTS.z) * .5;

    d = max(d, -sdBox(p - vec3(side, 7.5, 0), vec3(CUTOUT_B)));
    return d;
}

const vec2 e = vec2(1e-4, 0);

#define NORM(S, p) normalize(vec3(S(p + e.xyy) - S(p - e.xyy), \
                                  S(p + e.yxy) - S(p - e.yxy), \
                                  S(p + e.yyx) - S(p - e.yyx)))

vec2 uvScene(vec3 p)
{
    if (abs(p.z) > BBOX_MAX.z + .9)
    {
        if (abs(p.x) > BBOX_MAX.x - THICK) return p.yx * 40.;
        return p.xy * 40.;
    }
    if (abs(p.x) > BBOX_MAX.x - THICK) return p.yz * 40.;
    return p.xz * 40.;
}

