// ---------------------------------------------------------------------------------------
// Particle update
// ---------------------------------------------------------------------------------------

vec3 GRAVITY = vec3(0,-.0005,0); // strength of gravity
const float VISCOSITY = .003; // how viscous is the fluid
const float VISCOSITY_RADIUS = PARTICLE_COLL_SIZE * 8.; // how far away to apply viscosity

void particleStep(inout fxParticle p, fxState s, uint id, vec2 u);
vec4 neighborUpdate(fxParticle p, fxState s, uint typ, ivec2 ifc, uint id);

void mainImage(out vec4 O, vec2 u)
{
    ivec2 ifc = ivec2(u);
    uint idx = uint(ifc.x + ifc.y * int(R.x));
    uint id = idx / NUM_PARTICLE_DATA_TYPES; // which particle is this
    uint typ = idx - id * NUM_PARTICLE_DATA_TYPES; // which field of this particle are we working on
    computeMaxParticles();
    if(id >= g_MaxParticles) return;

    fxState s = fxGetState();
    fxParticle p = fxGetParticle(id);

    if (s.sim)
    {
        if (typ < POS)
        {
            O = neighborUpdate(p, s, typ, ifc, id);
            return;
        }
        else
        {
            particleStep(p, s, id, u);
        }
    }

    O = fxSaveParticle(p, typ);
}

// PARTICLE UPDATE

void particleStep(inout fxParticle p, fxState s, uint id, vec2 u)
{
    if (iFrame == 0 || s.res < 0.)
    {
        // init
        vec3 h = hash3( uvec3(u, iFrame) ) - .5;
        vec3 sb = BBOX_EXTENTS;
        sb.y = 30.;
        p.pos = h * sb + vec3(0, 0, 0);
        p.vel = vec3(0);//.08 * w, .10, .0) * .3;

        return;
    } 

    if (p.pos.y < -20.)
    {
        // reset particles that fall too far back to the top
        vec3 h = hash3( uvec3(u, iFrame) ) - .5;
        float w = (id % 2U) == 0U ? -1. : 1.;
        vec3 sb = BBOX_EXTENTS;
        sb.y = 10.;
        p.pos = h * sb + vec3(0, 20., 0.);
        p.vel = vec3(0);//.08 * w, .10, .0) * .3;
    }

    p.vel += GRAVITY;

    // collide with neighbors
    vec3 np = p.pos; // new pos
    vec3 visc = vec3(0); // viscosity impulse
    vec3 imp = vec3(0); // collision impulse
    for (int i = 0; i < 4; ++i)
    {
        for (int j = 0; j < 4; ++j)
        {
            uint ri = ((uint(i + iFrame) + id) * 7U) % 4U; // randomize the order we process neighbors to avoid directional effects
            uint nid = p.nbs[ri][j];
            if (nid == -1U) break;

            fxParticle nb = fxGetParticle(nid);
            float d2 = length2(nb.pos - p.pos);

            if (d2 < 1e-6) continue; // skip particles too close, because we lack a clear direction
            if (d2 > VISCOSITY_RADIUS * VISCOSITY_RADIUS) break; // no need to search farther once we've reached viscosity radius

            float d = sqrt(d2);

            visc += (nb.vel - p.vel) * smoothstep(VISCOSITY_RADIUS, 0., d) * VISCOSITY; // compute viscosity

            // collision
            if (d < PARTICLE_COLL_SIZE * 2.)
            {
                // position correction
                vec3 dir = normalize(p.pos - nb.pos);
                np = nb.pos + dir * PARTICLE_COLL_SIZE * 2.;

                vec3 relVel = p.vel - nb.vel;
                float normVel = min(0., dot(relVel, dir));

                imp -= dir * normVel; // collisio impulse
            }
        }
    }

    // update the position and velocity as affected by neighbors
    p.pos = np;
    p.vel += visc + imp;

    // damping
    p.vel *= .9995; // damping
    const float MAX_SPEED = .9; // clamping
    if (length(p.vel) > MAX_SPEED)
    {
        p.vel = normalize(p.vel) * MAX_SPEED;
    }

    // integrate
    p.pos += p.vel;

    // collide with boundary
    float d = sdPhysicsScene(p.pos);
    if (d < PARTICLE_COLL_SIZE)
    {
        vec3 sn = NORM(sdPhysicsScene, p.pos);
        p.pos = p.pos + (PARTICLE_COLL_SIZE - d) * sn;
        p.vel -= min(0., dot(sn, p.vel)) * sn;
    }
}

// NEIGHBOR UPDATE

bool iscoincidence(in uvec4 ids, uint cid, uint sid)
{
    return sid == cid || any(equal(ids,uvec4(sid)));
}

void insertion_sort(inout uvec4 i, inout vec4 d, uint i_, float d_)
{	
    if(any(equal(uvec4(i_),i))) return;
    if     (d_ < d[0])             
        i = uvec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = uvec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = uvec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = uvec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

void sortParticle(inout uvec4 ids, inout vec4 ds, uint typ, uint pid, uint sid, in fxParticle p)
{
    if(iscoincidence(ids, pid, sid)) return; //particle already sorted

    vec3 nbX = fxGetParticleData(sid, POS).xyz; 

    vec3 dx = nbX - p.pos;

    uint dir = uint(2.*(atan(dx.x, dx.z)+PI)/PI); 
    if(dir != typ) return; //not in this quadrant

    float t = length2(dx);

    insertion_sort(ids, ds, sid, t);
}

vec4 neighborUpdate(fxParticle p, fxState s, uint typ, ivec2 ifc, uint id)
{
    // nearest neighbors tracking
    // each particle tracks its 16 closest neighbors, 4 in each xz quadrant
    // dataType determines which quadrant we are computing
    uvec4 ids = uvec4(-1U);
    vec4 ds = vec4(1e6);

    if (iFrame > 0 && s.res > 0.)
    {
        // consider existing neighbors
        for (int i = 0; i < 4; ++i)
        {
            for (int j = 0; j < 4; ++j)
            {
                uint nid = p.nbs[i][j];
                if (nid == -1U) break;
                sortParticle(ids, ds, typ, id, nid, p);

                // consider neighbors' closest neighbor
                for (uint x = 0U; x < 4U; ++x)
                {
                    uint nbNid = uint(fxGetParticleData(nid, x));
                    if (nbNid != -1U)
                        sortParticle(ids, ds, typ, id, nbNid, p);
                }
            }
        }

        // random search
        int iter = iFrame < 10 ? 8 : 3;
        for(int k = 0; k < iter; k++)
        {
            uint h = hash(uvec4(ifc.x, ifc.y * int(R.x), iFrame, k)).x;
            uint hi = h % uint(g_MaxParticles);
            sortParticle(ids, ds, typ, id, hi, p);
        }
    }

    return vec4(ids);
}

