// ---------------------------------------------------------------------------------------
// Voronoi particle tracking buffer plus persistent state handling
// ---------------------------------------------------------------------------------------

void updateState(inout fxState s);
vec4 voronoi(fxState s, vec2 u);

void mainImage(out vec4 O, vec2 u)
{
    computeMaxParticles();
    fxState s = fxGetState();

    ivec2 ifc = ivec2(u);
    if(isStatePixel(ifc))
    {
        updateState(s);

        O = fxPutState(s, ifc);
    }
    else
        O = voronoi(s, u);
}

void updateState(inout fxState s)
{
    if (iFrame == 0 || // bootstrap
        abs(s.res) != R.x * R.y) // detect resolution change
    {
        s.res = -R.x * R.y;
        s.camAngle = vec2(0, .7);
        s.normal = false;
        s.sim = true;
        s.spec = true;
        s.ref = true;
        s.density = true;
        s.blur = true;
        s.eightNbs = true;
        s.paint = false;
        s.camMotion = true;
        s.mercury = false;
        s.noise = false;
        s.camOrbit = 20.;
    }
    else
    {
        // update persistent state
        s.res = abs(s.res); // positive res means, don't reset

        if (iMouse.z > 0.)
        {
            if (iMouse.w < 0.)
            {
                vec2 d = s.lastMouse - iMouse.xy / R;
                s.camAngle.x += .5 * d.x;
                s.camAngle.y += .2 * d.y;
            }

            s.camMotion = false;
            s.lastMouse = iMouse.xy / R;
        }

        if (keyClick(KEY_8)) s.eightNbs = !s.eightNbs;
        if (keyClick(KEY_B)) s.blur = !s.blur;
        if (keyClick(KEY_C)) s.camMotion = true;
        if (keyClick(KEY_D)) s.density = !s.density;
        if (keyClick(KEY_O)) s.noise = !s.noise;
        if (keyClick(KEY_P)) s.paint = !s.paint;
        if (keyClick(KEY_R)) s.ref = !s.ref;
        if (keyClick(KEY_S)) s.spec = !s.spec;
        if (keyClick(KEY_M)) s.mercury = !s.mercury;
        if (keyClick(KEY_N)) s.normal = !s.normal;
        if (keyClick(KEY_Z)) s.depth = !s.depth;
        if (keyClick(KEY_SPACE)) s.sim = !s.sim;
        if (keyDown(KEY_UP)) s.camOrbit -= .1;
        if (keyDown(KEY_DOWN)) s.camOrbit += .1;

        if (s.camMotion)
        {
            s.camAngle.x += .005 * s.camAngle.y;
            s.camAngle.y -= .005 * s.camAngle.x;
            s.camAngle = mix(s.camAngle, normalize(s.camAngle) * .2, .005);
        }
    }
}

float distance2Particle(uint id, vec2 u, mat4 w2c, vec3 ro, vec3 rd)
{
    // compute screen space position
    vec3 wp = fxGetParticleData(id, POS).xyz;
    vec3 sp = (w2c * vec4(wp,1.0)).xyz;
    sp.xy /= sp.z;

    vec2 t = sphIntersect(ro, rd, vec4(wp, PARTICLE_RENDER_SIZE));
    if (t.x < 0.) return 1e9; // don't consider particles that aren't hit by this ray

    return length2(sp.xy - u) + t.x * .0002;
}

#if FOUR_NBS
// four-neighbor voronoi search, enable to compare

void insertion_sort(inout uvec4 i, inout vec4 d, uint i_, float d_)
{	
    if(any(equal(uvec4(i_),i))) return;
    if     (d_ < d[0])             
        i = uvec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = uvec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = uvec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = uvec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

vec4 voronoi(fxState s, vec2 u)
{
    ivec2 ifc = ivec2(u);
    int wd = int(R.x);
	vec2 p = (2. * u - R.xy) / R.y;

    vec3 ro, fwd, lft, up;
    fxCalcCamera(s, wd, ro, fwd, lft, up);

    vec3 rd = fxCalcRay(u, R, fwd, lft, up);

    // camera-to-world and world-to-camera transform
    mat4 c2w = fxCalcCameraMat(ro, fwd, lft, up);
    mat4 w2c = inverse(c2w);

    // in this vector the four new closest particles' ids will be stored
    uvec4 new = uvec4(-1);
    // in this vector the distance to these particles will be stored 
    vec4 dis = vec4(1e6);

    if (iFrame > 0)
    {
        uvec4 old = fxGetClosest(ifc);      
        for (int j = 0; j < 4; j++)
        {
            uint id = old[j];
            if (id == -1U) break;
            if (id >= g_MaxParticles) continue;
            float dis2 = distance2Particle(id, p, w2c, ro, rd);
            insertion_sort(new, dis, id, dis2);

            // randomly check one of the physics neighbors of the particle, it's likely to be of interest
            #if 0
            uvec4 h = hash(uvec4(ifc.x, ifc.y * 3, iFrame, j));
            uvec4 nbs = uvec4(fxGetParticleData(id, h.x % 4U));
            uint nid = nbs[h.y % 4U];

            if (nid >= 0U && nid < g_MaxParticles)
            {
                float dis2 = distance2Particle(nid, p, w2c, ro, rd);
                insertion_sort(new, dis, nid, dis2);
            }
            #endif
        }

        // search nearby voronoi cells for particles that should move into our cell
        uint rng = 16u;
        uint cnt = max(2U, uint(R.y / 200.));

        for(uint i = 0u; i < cnt; ++i)
        {
            uvec4 h0 = hash(uvec4(ifc, iFrame, i) * i);
            uvec4 old = fxGetClosest(ifc + ivec2( h0.xy % rng - rng / 2u));      

            for (int j = 0; j < 1; j++)
            {
                uint id = old[j];
                if (id == -1U) break;
                if (id >= g_MaxParticles) continue;
                float dis2 = distance2Particle(id, p, w2c, ro, rd);
                insertion_sort(new, dis, id, dis2);
            }        
        }

        // random searching to kick start the process
        int searchIterations = iFrame < 5 ? 20 : 2;
        for(int k = 0; k < searchIterations; k++)
        {
            uint id = hash(uvec4(ifc.x, ifc.y * 3, iFrame, k)).x % g_MaxParticles;
            insertion_sort(new, dis, id, distance2Particle(id, p, w2c, ro, rd));
        }
    }

    return vec4(new);
}

#else // FOUR_NBS
// eight-neighbor voronoi search, enabled by default

void insertionSort(inout uvec4 i, inout vec4 d, inout uint i_, inout float d_)
{	
    uint ip = i_;
    float dp = d_;
    if     (d_ < d[0])             
        i_ = i.w,               d_ = d.w,
        i = uvec4(ip,i.xyz),    d = vec4(dp,d.xyz);
    else if(d_ < d[1])             
        i_ = i.w,               d_ = d.w,
        i = uvec4(i.x,ip,i.yz), d = vec4(d.x,dp,d.yz);
    else if(d_ < d[2])            
        i_ = i.w,               d_ = d.w,
        i = uvec4(i.xy,ip,i.z), d = vec4(d.xy,dp,d.z);
    else if(d_ < d[3])           
        i_ = i.w,               d_ = d.w,
        i = uvec4(i.xyz,ip),    d = vec4(d.xyz,dp);
}

void insertionSort2(inout uvec4 na, inout uvec4 nb, inout vec4 da, inout vec4 db, uint id, vec2 p, mat4 w2c, vec3 ro, vec3 rd)
{
    if (any(equal(uvec4(id),na)) || any(equal(uvec4(id),nb))) return; // don't insert the same id twice

    float d = distance2Particle(id, p, w2c, ro, rd);
    insertionSort(na, da, id, d);
    insertionSort(nb, db, id, d);
}

// consider a new set of four candidate particles to insert into our arrays
void sortNbs(uvec4 old, vec2 p, inout uvec4 na, inout uvec4 nb, inout vec4 da, inout vec4 db, mat4 w2c, vec3 ro, vec3 rd)
{
    for (int j = 0; j < 4; j++)
        insertionSort2(na, nb, da, db, old[j], p, w2c, ro, rd);
}

// consider a new set of eignt candidate particles to insert into our arrays
void sortNbs2(uvec4 a, uvec4 b, inout uvec4 na, inout uvec4 nb, inout vec4 da, inout vec4 db, vec2 p, mat4 w2c, vec3 ro, vec3 rd)
{
    sortNbs(a, p, na, nb, da, db, w2c, ro, rd);
    sortNbs(b, p, na, nb, da, db, w2c, ro, rd);
}

vec4 voronoi(fxState s, vec2 u)
{
    ivec2 ifc = ivec2(u);
	vec2 p = (2. * u - R) / R.y;

    vec3 ro, fwd, lft, up;
    fxCalcCamera(s, int(R.x), ro, fwd, lft, up);

    vec3 rd = fxCalcRay(u, R, fwd, lft, up);

    // camera-to-world and world-to-camera transform
    mat4 c2w = fxCalcCameraMat(ro, fwd, lft, up);
    mat4 w2c = inverse(c2w);

    // in this vector the four new closest particles' ids will be stored
    uvec4 na = uvec4(-1), nb = uvec4(-1);
    // in this vector the distance to these particles will be stored 
    vec4 da = vec4(1e6), db = vec4(1e6);

    if (iFrame > 0)
    {
        uvec4 oa, ob;
        fxGetClosest(ifc, oa, ob);      
        sortNbs2(oa, ob, na, nb, da, db, p, w2c, ro, rd);

        // search nearby voronoi cells for particles that should move into our cell
        uint rng = 16u;
        uint cnt = uint(R.y / 100.);

        for(uint i = 0u; i < cnt; ++i)
        {
            uvec4 h0 = hash(uvec4(ifc, iFrame, i) * i);
            uvec4 oa, ob;
            fxGetClosest(ifc + ivec2( h0.xy % rng - rng / 2u), oa, ob);      
            sortNbs2(oa, ob, na, nb, da, db, p, w2c, ro, rd);     
        }

        // random searching to kick start the process
        int searchIterations = iFrame < 5 ? 20 : 5;
        for(int k = 0; k < searchIterations; k++)
        {
            uint id = uint(hash(uvec4(ifc.x, ifc.y * 3, iFrame, k)).x) % g_MaxParticles;
            insertionSort2(na, nb, da, db, id, p, w2c, ro, rd);
        }
    }

    if (!s.eightNbs) nb = uvec4(-1);
    return fxSaveClosest(na, nb);
}
#endif // FOUR_NBS

