// ---------------------------------------------------------------------------------------
// G buffer render
// ---------------------------------------------------------------------------------------

// draw one particle
void renderParticle(uint id, vec3 fwd, vec3 ro, vec3 rd, inout fxGBufferPixel pix)
{
    fxParticle p = fxGetParticle(id);
    vec2 t = sphIntersect(ro, rd, vec4(p.pos, PARTICLE_RENDER_SIZE));
    vec3 hit = ro + rd * t.x;
    float z = t.x; // construct z from t
    pix.d += t.y - t.x;

    if (z > 0.)
    {
        if (z < pix.t)
        {
            pix.bz = pix.z = z * dot(fwd, -rd);
            pix.t = z;
        }
    }
}

// draw a sett of four particles
void renderFourParticles(uvec4 ids, vec3 fwd, vec3 ro, vec3 rd, inout fxGBufferPixel pix)
{
    for (int j = 0; j < 4; j++)
    {
        uint id = ids[j];
        if (id == -1U || id >= g_MaxParticles) break;
        renderParticle(id, fwd, ro, rd, pix);
    }
}

void mainImage(out vec4 O, vec2 u)
{
    vec3 ro, fwd, lft, up;
    fxCalcCamera(fxGetState(), int(R.x), ro, fwd, lft, up);

    vec3 rd = fxCalcRay(u, R, fwd, lft, up);

    fxGBufferPixel pix;
    pix.z = 1e6;
    pix.t = 1e6;
    pix.d = 0.;

    // render particles
#if FOUR_NBS
    uvec4 clo = fxGetClosest(ivec2(u));
    renderFourParticles(clo, fwd, ro, rd, pix);
#else
    uvec4 oa, ob;
    fxGetClosest(ivec2(u), oa, ob);      
    renderFourParticles(oa, fwd, ro, rd, pix);
    renderFourParticles(ob, fwd, ro, rd, pix);
#endif

    O = fxSaveGBuffer(pix);
}

