// ---------------------------------------------------------------------------------------
// Density and depth gaussian blur
// ---------------------------------------------------------------------------------------

// clips the boundaries of particles, making individual particles look smaller
// this helps balance out the fact that we render them deeply intersecting, so
// if we don't make them smaller when separated, they would appear to gain volume
const float MIN_DEPTH = .8;

// how far apart a nearby particle can be, in z depth, before it can be blured with us
// too low, and you will see particle boundaries not blurring together
// too high, and foreground particles will merge too much with background particles
const float BLUR_TOLERANCE_REL = .1;
const float BLUR_TOLERANCE_ABS = PARTICLE_RENDER_SIZE * .5;

// kernel from Gaussian Blur by mrharicot
// https://www.shadertoy.com/view/XdfGDH
float normpdf(float x, float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

float invnormpdf(float n, float sigma)
{
	return sqrt(-2. * log(n * sigma / 0.39894) * sigma * sigma);
}

vec4 blur(vec2 u)
{
    fxGBufferPixel pix = fxGetGBuffer(u);
    if (pix.d < MIN_DEPTH) return vec4(1e6);

    float sigma = .25 * iResolution.y / abs(pix.z); // blur falloff depends on resolution and depth

    const float kMin = .0001; // min kernel value we care about sampling
    int mSize = int(ceil(invnormpdf(kMin, sigma))); // compute convolution size that reaches kMin
    int kSize = (mSize - 1) / 2;
    pix.d = 0.;
    pix.bz = 0.;

    float Z = 0.;

    for (int i = -kSize; i <= kSize; ++i)
    {
        for (int j = -kSize; j <= kSize; ++j)
        {
            float k = normpdf(float(i), sigma) * normpdf(float(j), sigma);
            if (k < kMin) continue; // save the texture fetch when the kernel is small anyway
            fxGBufferPixel n = fxGetGBuffer(u + vec2(i, j));

            if (abs(n.z - pix.z) < BLUR_TOLERANCE_ABS ||
                abs((n.z - pix.z) / pix.z) < BLUR_TOLERANCE_REL) // exclude pixels not near our z depth
            {
                pix.d += k * n.d;
                pix.bz += k * n.z;
                Z += k;
            }
        }
    }

    pix.bz /= Z;
    pix.d /= Z;
    return fxSaveGBuffer(pix);
}

void mainImage( out vec4 O, vec2 u )
{
    fxState s = fxGetState();
    if (!s.blur)
    {
        fxGBufferPixel pix = fxGetGBuffer(u);
        if (pix.d < MIN_DEPTH)
            O = vec4(1e6);
        else
            O = fxSaveGBuffer(pix);
    }
    else
        O = blur(u);
}

