float bicubic(float a, float b, float c, float d, float t){
    t += 1.;
    float one   = t-1.;
    float two   = t-2.;
    float three = t-3.;
    //return (-(t-1.)*(t-2.)*(t-3.)*a + t*(t-1.)*(t-2.)*d + 3.*t*(t-2.)*(t-3.)*b - 3.*t*(t-1.)*(t-3.)*c)/6.;
    // https://www.desmos.com/calculator/5d6ph151vi interactive :D
    return (-one*two*three*a + t*one*two*d + 3.*t*two*three*b - 3.*t*one*three*c)/6.;
}

vec4 bicubic(vec4 a, vec4 b, vec4 c, vec4 d, float t){
