    float l = 1.-smoothstep(0.,0.002,uv.y-(o));
    float v = cos(uv.x*9.)*.08-.089+i(vec2(f,r(f+5.8)),vec2(f+1.,r(f+6.8)),uv.x*50.+1.)*.04-0.02;
    float p = 1.-smoothstep(0.,0.002,uv.y-(v));
    vec3 col = mix(upper, lower,uv.y*5.);
