{
    float currentHeight = height(pos);
    return vec3((height(pos + vec2(epsilon, 0.0)) - currentHeight) * -1.0, epsilon / 10.0, (height(pos + vec2(0.0, epsilon)) - currentHeight) * -1.0);
}

