    dir.z = old.z * cos(rot.y) + old.y * -sin(rot.y);
    old = dir;
    dir.x = old.x * cos(rot.x) + old.z * sin(rot.x);
    dir.z = old.z * cos(rot.x) + old.x * -sin(rot.x);

    float initialMove = startPos.y - 2.0;

    int rendertype = 0;

    int marchStep = 0;
    float distStep = 0.02;
    bool stop = false;
