    if (rendertype == 2)
    {

        if (noise(pos.xz * 0.1 + iTime * vec2(0.1, 0.1)) > 0.0)
        {
            bg = 1.0 - sqrt(noise(pos.xz * 0.1 + iTime * vec2(0.1, 0.1)));
            endCol = vec3(sqrt(noise(pos.xz * 0.1 + iTime * vec2(0.1, 0.1))));
        }
    }


       endCol += bg * vec3(0.0, 0.6, 0.99) + vec3(0.0, 0.4, 0.0) * dot(dir, vec3(0.0, 1.0, 0.0));//bg * vec3(0.5 + 0.25 * dot(dir, vec3(0.0, 1.0, 0.0)), 0.75 + 0.25 * dot(dir, vec3(0.0, 1.0, 0.0)), 1.0);
       endCol.rgb += bg * vec3(1.0, 1.0, -0.6) * (max(dot(dir, sunDir), 0.99) - 0.99) * 50.0;

    fragColor = vec4(endCol, 1.0);
}

