//
// This shader is part of a tutorial on YouTube
// https://youtu.be/PGtv-dBi2wE

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

float Capsule(vec3 position, vec3 bottom, vec3 top, float radius) {
    vec3 size = top - bottom;
    vec3 dist = position - bottom;

    float cylinder = dot(size, dist) / dot(size, size);
    cylinder = clamp(cylinder, 0., 1.);

    vec3 capsule = bottom + cylinder * size;

    return length(position - capsule) - radius;
}

float Torus(vec3 position, vec2 radius) {    
    float base = length(position.xz) - radius.x;
    return length(vec2(base, position.y))-radius.y;
}

float Box(vec3 position, vec3 size) {    
    return length(max(abs(position) - size, 0.));
}

float Plane(vec3 position) {    
    return position.y;
}

float Sphere(vec3 position, vec4 size) {
    return length(position - size.xyz) - size.w;
}

float GetDist(vec3 position) {
    float plane = Plane(position-vec3(0, -1, 0));

    float sphere = Sphere(position-vec3(4, .5, 3), vec4(0, 1, 6, 1));
    float capsule = Capsule(position, vec3(2, 1, 6), vec3(0, 2, 6), sin(iTime)+1.);
    float torus = Torus(position-vec3(0, .5, 6), vec2(2, .2));
    float box = Box(position-vec3(-2, 1, 3), vec3(.2, 1, .2));

    float scene = plane;

    scene = min(scene, capsule);
    scene = min(scene, torus);
    scene = min(scene, sphere);
    scene = min(scene, box);

    return scene;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 3, 6);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1.5, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

#define HUGE 1000000.0
#define PI 3.14159

vec3 ballPos;
vec3 lightDir = normalize(vec3(1,2,-2));

// computes the ray direction of a screen pixel
vec3 perspectiveRay(vec2 pixel)
{
    float x = -1.0 + 2.0*(pixel.x / iResolution.x);
    float y = 1.0;
    float z = -1.0 + 2.0*(pixel.y / iResolution.y);
    x *= iResolution.x / iResolution.y;
    return normalize(vec3(x,y,z));
}

vec3 rotateYaw(vec3 v, float angle)
{
    return vec3(
        v.x*cos(angle) - v.y*sin(angle),
        v.y*cos(angle) + v.x*sin(angle),
        v.z);
}

vec3 rotatePitch(vec3 v, float angle)
{
    return vec3(
        v.x,
        v.y*cos(angle) - v.z*sin(angle),
        v.z*cos(angle) + v.y*sin(angle));
}

vec3 rotatePitchAbout(vec3 v, vec3 about, float angle)
{
    return rotatePitch(v - about, angle) + about;
}

vec3 rotateYawAbout(vec3 v, vec3 about, float angle)
{
    return rotateYaw(v - about, angle) + about;
}

float calcLight(vec3 normal, vec3 rd, float shininess)
{
    float ambient  = 0.3;
    float diffuse  = max(dot(-lightDir,normal), 0.0);
    float specular = max(dot(-lightDir,reflect(rd, normal)), 0.0);
    specular *= specular*specular*specular*specular*specular*specular*specular*specular;
    return ambient + diffuse + specular * shininess;
}

float raycastFloor(vec3 ro, vec3 rd, out vec2 hit)
{
    if (rd.z >= 0.0)
        return HUGE;
    float t = -ro.z / rd.z;
    if (t < 0.0)
        return HUGE;
    hit = vec2(ro.x+rd.x*t, ro.y+rd.y*t);
    return t;
}

float raycastSphere(vec3 ro, vec3 rd, vec3 center, float radius, out vec3 normal)
{
    vec3 sc = center;  // sphere center
    float sr = radius;  // sphere radius

    // Imagine a plane that is perpendicular to the ray and intersects the sphere's center.
    // ts is the distance along the ray to that plane.
    float ts = dot(sc-ro, rd);
    if (ts < 0.0)
        return HUGE;
    // distance squared between point at ts and the sphere center
    float r2 = (dot(sc-ro,sc-ro) - ts*ts);
    if (r2 > sr*sr)
        return HUGE;
    float t = ts - sqrt(sr*sr - r2);  // distance along ray to where it intersects the sphere
    vec3 hit = ro + rd * t;
    normal = normalize(hit-sc);
    return t;
}

// like raycastSphere, but intersects with the far surface of the sphere
float raycastSphereInner(vec3 ro, vec3 rd, vec3 center, float radius, out vec3 normal)
{
    vec3 sc = center;  // sphere center
    float sr = radius;  // sphere radius

    // Imagine a plane that is perpendicular to the ray and intersects the sphere's center.
    // ts is the distance along the ray to that plane.
    float ts = dot(sc-ro, rd);
    if (ts < 0.0)
        return HUGE;
    // distance squared between point at ts and the sphere center
    float r2 = (dot(sc-ro,sc-ro) - ts*ts);
    if (r2 > sr*sr)
        return HUGE;
    float t = ts + sqrt(sr*sr - r2);  // distance along ray to where it intersects the sphere
    vec3 hit = ro + rd * t;
    normal = normalize(sc-hit);
    return t;
}

// Returns true or false to select the pattern for the floor position
bool checker(vec2 floorPos)
{
    return mod(floor(floorPos.x) + floor(floorPos.y), 2.0) < 1.0;
}

float sphereSdf(vec3 pos, vec3 sphereCenter, float radius)
{
    return distance(pos, sphereCenter) - radius;
}

// Credit to Inigo Quilez for the SDF functions (https://iquilezles.org/articles/distfunctions/)

// slow...
float ellipsoidSdf(vec3 pos, vec3 center, vec3 radius)
{
    vec3 p = pos - center;
    float k0 = length(p/radius);
    float k1 = length(p/(radius*radius));
    return k0 * (k0 - 1.0) / k1;
}

float cylinderSdf(vec3 pos, vec3 center, float height, float radius)
{
    vec3 p = pos - center;
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(radius,height);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float roundConeSdf(vec3 pos, vec3 center, float r1, float r2, float h)
{
    vec3 p = pos - center;
    // sampling independent computations (only depend on shape)
    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);

    // sampling dependant computations
    vec2 q = vec2( length(p.xy), p.z );
    float k = dot(q,vec2(-b,a));
    if( k<0.0 ) return length(q) - r1;
    if( k>a*h ) return length(q-vec2(0.0,h)) - r2;
    return dot(q, vec2(a,b) ) - r1;
}

float capsuleSdf(vec3 pos, vec3 a, vec3 b, float r)
{
    vec3 p = pos;
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float opSmoothUnion(float d1, float d2, float k)
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}
