// The built-in 2-argument atan function is incredibly slow (at least on my Intel HD 3000).
// The single argument version is much faster.
float atan_fast(float a, float b)
{
    float result = atan(a / b);
    if (b < 0.0)
        result += PI;
    return result;
}

// samples a 2D x,y position and returns a value between 0 and 1
float spiral(vec2 pos)
{
	float radius = length(pos);
	float angle = /*atan*/atan_fast(pos.y, pos.x) + PI*0.75;
    float x = radius * 4.0 - angle;
    // make it like a wave with sharp peaks
    x = fract(x / (2.0*PI));
    return 1.0 + 4.0 * x * (x - 1.0);
}

float animLimbAngle(void)
{
    return sin(iTime * 3.0 * PI);
}

float animEars(void)
{
    float t = fract(iTime / 3.0) * 3.0;
    t *= 50.0;
    if (t >= 4.0 * PI)
        t = 0.0;
    return sin(t);
}

float animBlink(void)
{
    const float blinkSpeed = 0.1;
    const float frequency = 3.5;
    float t = fract(iTime / frequency) * frequency;

    return 2.0 * max((blinkSpeed-abs(t-blinkSpeed))/blinkSpeed, 0.0);
}
