float xzdiscSdf(vec3 pos, vec3 center, float radius, float s)
{
    radius -= s;
    vec3 p = pos - center;
    //float height = 0.5;
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(radius,0.0);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - s;
}

float globalSdf(vec3 pos, out vec3 color)
{
    pos.y -= ballPos.y;
    pos.z -= 2.0;

    vec3 bodyColor  = vec3(0.3, 0.2, 0.1);
    vec3 skinColor  = vec3(0.8, 0.7, 0.6);
    vec3 shirtColor = vec3(0.8, 0.4, 0.2);
    vec3 eyeColor   = vec3(0.1, 0.1, 0.1);
    vec3 white      = vec3(1.0, 1.0, 1.0);

    color = bodyColor;

    vec3 ear      = vec3(5.0,  0.0, 14.0);
    vec3 shoulder = vec3(1.5,  0.0, 10.0);
    vec3 elbow    = vec3(3.5,  0.0,  8.0);
    vec3 hand     = vec3(4.5, -1.0,  6.0);
    vec3 hip      = vec3(2.0,  0.0,  4.0);
    vec3 knee     = vec3(2.1, -0.5,  2.0);
    vec3 ankle    = vec3(2.2,  0.0,  0.0);
    vec3 toes     = vec3(2.2, -2.0, -0.5);

    // Animate limbs
    float angle = animLimbAngle();
    pos.x += angle * 0.25;  // make body shake a little bit
    if (pos.x < 0.0)
        angle = -angle;  // cheap trick to make the two sides animate opposite from each other
    elbow = rotatePitchAbout(elbow, shoulder, angle);
    hand = rotatePitchAbout(hand, shoulder, angle);
    knee = rotatePitchAbout(knee, hip, -angle);
    ankle = rotatePitchAbout(ankle, hip, -angle);
    toes = rotatePitchAbout(toes, hip, -angle);
    // Animate ears
    ear = rotateYawAbout(ear, vec3(2,0,14), animEars() * 0.15);
    // Animate blinking
    float blink = animBlink();

    vec3 posMirrored = vec3(abs(pos.x), pos.y, pos.z);

    float d = HUGE;

    // head
    float dHead = cylinderSdf(pos, vec3(0, 0, 15), 1.0, 0.8) - 2.0;
    dHead = opSmoothUnion(dHead, ellipsoidSdf(pos, vec3(0, -1, 12.5), vec3(2.5, 2.0, 1.0)), 2.0);
    // hair tuft
    float dTuft = opSmoothUnion(
        sphereSdf(pos, vec3(0, -1.5, 18.2), 0.5),
        capsuleSdf(pos, vec3(0, 0, 18.5), vec3(0, 1, 19), 0.0),
        2.5);
    dHead = min(dHead, dTuft);
    //dHead = HUGE;
    d = min(d, dHead);
    // ears
    float dEar = xzdiscSdf(posMirrored, ear, 2.5, 0.5);
    if (dEar < 0.1 && posMirrored.y < ear.y)
    {
        // Cut out the swirl in the iear
        // Doing this breaks the SDF a bit, but it approximates the look very well.
        dEar += 0.5 * spiral(ear.xz - posMirrored.xz);
    }
    //dEar = HUGE;
    d = min(d, dEar);
    // body
    float dBody = roundConeSdf(pos, vec3(0,0,6.5), 3.2, 2.2, 3.0);
    //dBody = HUGE;
    d = min(d, dBody);
    // arms
    float dArm = min(
        capsuleSdf(posMirrored, shoulder, elbow, 0.5),
        capsuleSdf(posMirrored, elbow, hand, 0.5));
    //dArm = HUGE;
    d = min(d, dArm);
    // legs
    float dLeg = min(
        capsuleSdf(posMirrored, hip, knee, 0.6),
        capsuleSdf(posMirrored, knee, ankle, 0.6));
    //dLeg = HUGE;
    d = min(d, dLeg);
    // hands
    float dHand = sphereSdf(posMirrored, hand, 1.0);
    //dHand = HUGE;
    d = min(d, dHand);
    // feet
    float dFoot = sphereSdf(posMirrored, ankle, 1.0);
    dFoot = opSmoothUnion(dFoot, capsuleSdf(posMirrored, toes - vec3(0.5,0,0), toes + vec3(0.5,0,0), 0.5), 2.0);
    //dFoot = HUGE;
    d = min(d, dFoot);

    // colors
    if (dHead <= d && pos.y < -1.0)
    {
        if (pos.y < -1.0)
        {
            vec3 eye = vec3(1.2 * sign(pos.x), -2.0, 14.7);
            vec3 dist = pos - eye;
            dist.z *= 0.6;
            if (pos.z <= eye.z + 1.0 - blink
             && dist.x*dist.x + dist.z*dist.z < 0.25 && abs(dist.z)*2.0 > -dist.x)
                color = eyeColor;
            // mouth
            else if (pos.z < 13.3 && abs(pos.x*pos.x*pos.x*pos.x*0.5*0.5*0.5*0.5 - pos.x*pos.x*0.5*0.5 - pos.z + 13.0) < 0.1)
                color = vec3(0.2, 0.2, 0.2);
            else if (pos.z < 15.0 || distance(pos, eye) < 2.2)
                color = skinColor;
        }
    }
    if (dEar <= d)
    {
        if (pos.y < ear.y + 0.1)
            color = skinColor;
    }
    if (dBody <= d)
    {
        if (pos.z > 6.5)
        {
            color = shirtColor;
            // letter A
            vec2 letter = posMirrored.xz;
            letter.y = (letter.y - 8.0) / 2.0;
            if (pos.y < 0.0
             && letter.y >= 0.0 && letter.y <= 1.0
             && letter.x < 1.2 - letter.y)
            {
                if ((letter.x > 0.6 - letter.y)
                 || (letter.y >= 0.2 && letter.y <= 0.4))
                color = white;
            }
        }
        // random trick I found by accident to color the butt
        else if (pos.y > 0.0 && dEar + 0.3 < dHead)
            color = skinColor;
    }
    if (dArm <= d)
    {
        if (distance(posMirrored, shoulder) < 2.5)
            color = shirtColor;
    }
    if (dHand <= d || dFoot <= d)
        color = skinColor;
    return d;
}

vec3 calcNormal(vec3 pos, float d)
{
    float epsilon = 0.001;
    vec3 dummy;
    vec3 v = vec3(
        globalSdf(pos - vec3(epsilon, 0.0, 0.0), dummy),
        globalSdf(pos - vec3(0.0, epsilon, 0.0), dummy),
        globalSdf(pos - vec3(0.0, 0.0, epsilon), dummy));
    return normalize(d - v);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // ball motion
    ballPos = vec3(0.0, -40.0*iTime, 12.0);
    float ballRadius = 40.0/PI;

    // camera motion
    float yaw = -iTime * 0.25;
    float pitch = -0.6;
    if (iMouse.z > 0.0)
    {
        yaw = 3.0 * (iMouse.x - abs(iMouse.z)) / iResolution.x;
        pitch = 3.0 * (iMouse.y - abs(iMouse.w)) / iResolution.y;
        pitch = clamp(pitch, -3.14159/2.0, 0.0);
    }

    // ray origin
    vec3 ro = vec3(0,-20,0);
    ro = rotatePitch(ro, pitch);
    ro = rotateYaw(ro, yaw);
    ro.y += ballPos.y;
    ro.z += ballPos.z;
    // ray direction
    vec3 rd = perspectiveRay(fragCoord);
    rd = rotatePitch(rd, pitch);
    rd = rotateYaw(rd, yaw);
    rd = normalize(rd);

    vec3 normal;

    vec3 sky = mix(vec3(1,1,1),rd.z > 0.0 ? vec3(0,0,1) /*sky*/ : vec3(0,0.5,0) /*land*/,abs(rd.z));
    vec3 color = sky;
    float tBall;

    // draw floor
    vec2 floorPos;
    float tFloor;
    if ((tFloor = raycastFloor(ro, rd, floorPos)) < HUGE && abs(floorPos.x) < 60.0)
    {
        vec2 uv = fract(floorPos / 10.0);
        color = checker(floorPos / 10.0) ? vec3(0.2, 0.4, 0.2) : vec3(0.3, 0.6, 0.3);
        color *= 0.6 + texture(iChannel0, uv).r * 0.4;

        float light;
        if (raycastSphere(vec3(floorPos, 0.0), -lightDir, ballPos, ballRadius, normal) < HUGE)
            light = 0.5;  // ball shadow
        else
            light = calcLight(vec3(0, 0, 1), rd, 0.3);

        color *= light;

        // fade out with sky to avoid aliasing artifacts
        float fadeStart = 200.0;
        float fadeEnd = 400.0;
        color = mix(color, sky, clamp((tFloor-fadeStart)/(fadeEnd-fadeStart),0.0,1.0));

    }
    // draw ball inner surface
    if ((tBall = raycastSphereInner(ro, rd, ballPos, ballRadius-0.3, normal)) < HUGE)
    {
        float light = calcLight(normal, rd, 0.8);
        normal = rotatePitch(normal, ballPos.y/ballRadius);
        vec3 ballColor = normal.z > 0.0 ? vec3(1.0,0.2,0.2) : vec3(0.8);
        color = mix(color, ballColor * light, 0.5);
    }

    // Ray-march the monkey. Since the monkey is always inside the ball, we only need to do this if the ray actually touches the ball.
    if (tBall < HUGE)
    {
        float t = 0.0;
        for (int i = 0; i < 30; i++)
        {
            vec3 pos = ro + rd * t;
            vec3 objColor;
            float d = globalSdf(pos, objColor);
            if (d < 0.1)
            {
                if (tFloor < t)
                    break;
                color = objColor;
                vec3 normal = calcNormal(pos, d);
                color *= calcLight(normal, rd, 0.5);
                break;
            }
            t += d;
            if (t > 30.0)
                break;
        }
    }

    // draw ball outer surface
    if (raycastSphere(ro, rd, ballPos, ballRadius, normal) < HUGE)
    {
        float light = calcLight(normal, rd, 0.8);
        normal = rotatePitch(normal, ballPos.y/ballRadius);
        vec3 ballColor = normal.z < 0.0 ? vec3(1.0,0.2,0.2) : vec3(0.8);
        color = mix(color, ballColor * light, 0.5);
    }
    fragColor = vec4(color, 1.0);
}

