void mainImage( out vec4 O, in vec2 F ) {

    vec3 C = vec3(.095);
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    uv +=T*vec2(.08,.05);
    uv*= R.x<1300.? 2.25 : 3.5;

    vec2 vuv = uv;

    // threshold values
    const vec2 rndX[4]=vec2[4]( vec2(.5, .45), vec2(.5, .25), vec2(.5, .75), vec2(.5, 1));

    // distance field values.
    vec4 d=vec4(1e5), s=vec4(1e5), b=vec4(1e5);
    // hash values
    vec4 hd=vec4(1e5), hs=vec4(1e5), hb=vec4(1e5);
    float level=1.;

    // Main loop and neighbor checking from @Shane's shader.
    // https://www.shadertoy.com/view/4t3BW4

    for(int k=0; k<4; k++){
        vec2 id = floor(vuv*level);
        float frnd = hash22(id).x;
        for(int j=-1; j<=1; j++){
            for(int i=-1; i<=1; i++){

                // neighboring cell ID.
                vec2 neighbors = vec2(i,j);
                vec2 home = id+neighbors;
                vec2 rnd = hash22(home);

                // neighboring cell hash values.
                vec2 rnd2 = hash22(floor(home/2.));
                vec2 rnd4 = hash22(floor(home/4.));
                vec2 rnd8 = hash22(floor(home/8.));

                // If the previous large tile has been rendered, continue.
                if(k==1 && rnd2.y<rndX[0].y) continue;
                // If any of the two previous larger tiles have been rendered, continue.
                if(k==2 && (rnd2.y<rndX[1].y || rnd4.y<rndX[0].y)) continue;
                // If any of the three previous larger tiles have been rendered, continue.
                if(k==3 && (rnd2.y<rndX[2].y || rnd4.y<rndX[1].y  || rnd8.y<rndX[0].y )) continue;

                // threshold
                if(rnd.y<rndX[k].y) {

                    vec2 p = vuv -(id+.5+neighbors)/level;
                    if(rnd.x<rndX[k].x) p.x = -p.x;

                    float bx = (.43-(level*.015))/level;
                    float square = box(p,vec2(bx))-(.075/level);

                    float tk = k<3?.1/level:.075/level;
                    float hf = .50/level;
                    float qf = .25/level;
                    float mf = .05/level;

                    vec2 pv = length(p-vec2(hf,-hf))<length(p+vec2(hf,-hf))? p-vec2(hf,-hf):p+vec2(hf,-hf);

                    float cd = abs(length(pv)-hf)-tk;
                    if(rnd.x>.8) cd = min(length(p.x)-tk,length(p.y)-tk);

                    float bd = length(abs(p)-hf)-qf;

                    float fk = float(k);
                    float trnd = fract(fk+rnd.x*43.72);

                    if(trnd>.7) cd=abs(cd)-mf;
                    if(trnd<.15) cd=abs(abs(cd)-mf)-(.025/level);

                    cd = max(square,cd);
                    bd = max(square,bd);

                    s[k] = min(s[k], square);
                    d[k] = min(d[k], cd);
                    b[k] = min(b[k], bd);

                    hd[k] = fract((trnd*53.1)+rnd.x+frnd);
                    hs[k] = fract((rnd2.y+trnd+rnd.x+frnd)*47.3);
                    hb[k] = fract(rnd2.x*19.7+rnd.x+frnd);
                }
            }
        }    

        level*=2.;
    }

    float px = fwidth(vuv.x);
    float ft = .4+T*.1;

    for(int k=0; k<4; k++){
        C = mix(C,C*.5,smoothstep(.01+px,-px,s[k]-.0005));

        vec3 ca = hsv(vec3((ft+hs[k])*.15,1.,.5));
        vec3 cb = hsv(vec3((ft+hd[k])*.35,1.,.6));
        vec3 cc = hsv(vec3((ft+hb[k])*.25,1.,.3));

        C = mix(C,ca,smoothstep(px,-px,s[k]));
        if(k<3) C = mix(C,cc,smoothstep(px,-px,b[k]));
        C = mix(C,cb,smoothstep(px,-px,d[k]));

        C = mix(C,vec3(.05),smoothstep(px,-px,abs(s[k])-.0025));
    }

    // Output to screen
    C = pow(C,vec3(.4545));
    O = vec4(C,1.0);
}

