vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b * cos(6.28318 * (c * t + d));
}

vec4 pal(in vec4 i, in vec2 uv) {
    const vec3 a = vec3(0.5,0.5,0.5), b = vec3(0.5,0.5,0.5), c = vec3(1.0,1.0,1.0), d = vec3(0.0,0.33,0.67);

    const vec3 c64pal[16] = vec3[16](
        vec3(0., 0., 0.), vec3(1., 1., 1.), vec3(0., 1., 0.), vec3(.5, 0., 0.),
        vec3(.6, 1., .9), vec3(.7, .3, .7), vec3(0., .7, .4), vec3(0., 0., .6),
        vec3(.9, .9, .5), vec3(.8, .5, .3), vec3(1., .5, .5), vec3(.2, .2, .2),
        vec3(.5, .5, .5), vec3(.6, 1., .7), vec3(0., .5, 1.), vec3(.8, .8, .8)
    );
    vec3 result = vec3(0);
    vec4 j = vec4(1.) - i;
    for (int k = 0; k < 16; ++k) {
        vec4 w4 = mix(i, j, equal(k & ivec4(8, 4, 2, 1), ivec4(0)));
        float w = w4.x * w4.y * w4.z * w4.w;
#if 0
        result += w * pal(float(k) / 16.0, vec3(0.5),vec3(0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.25) );
#else
        result += w * c64pal[k];
#endif
    }
    return vec4(result, 1.0);
}

// Anti-aliasing version of a simple threshold.  Look at the slope
// of the underlying curve being thresholded to figure out how much
// of the pixel the threshold boundary would capture, so that we
// don't get aliasing around the horizon.
//
vec4 aastep(vec4 threshold, vec4 f) {
    vec4 margin = clamp(fwidth(f) * 0.7, 0.0, 0.4);
    return smoothstep(threshold - margin, threshold + margin, f);
}
vec4 aastep(float threshold, vec4 f) {
    return aastep(vec4(threshold), f);
}


vec2 w0(vec2 a) { return (a * (a * (-a + 3.0) - 3.0) + 1.0) / 6.0; }
vec2 w1(vec2 a) { return (a * a * (3.0 * a - 6.0) + 4.0) / 6.0; }
vec2 w2(vec2 a) { return (a * (a * (-3.0 * a + 3.0) + 3.0) + 1.0) / 6.0; }
vec2 w3(vec2 a) { return (a * a * a) / 6.0; }

vec4 texbicubic(sampler2D tex, vec2 uv, vec2 tsc) {
    bool linear = texelFetch(iChannel3, ivec2(32,0),0).x >= 0.5;  // space
    if (linear) return texture(tex, uv * tsc);

    // If you zoom in or set `dimension` very low, then you'll see
    // line segments rolling around the edges of the curve.  That's
    // the linear interpolation of a low-resolution buffer, and
    // bicubic sampling fixes that very nicely.
    //
    // Also, the meniscus shading brings out artefacts you shouldn't
    // otherwise be able to see, so this would help a lot there.

    uv *= dimension;
    tsc /= dimension;
    uv += 0.5;

	vec2 iuv = floor(uv);
	vec2 fuv = fract(uv);

    vec2 g0 = w0(fuv) + w1(fuv);
    vec2 g1 = w2(fuv) + w3(fuv);
    vec2 h0 = -1.0 + w1(fuv) / (w0(fuv) + w1(fuv));
    vec2 h1 = 1.0 + w3(fuv) / (w2(fuv) + w3(fuv));

	vec2 p00 = iuv + h0 - 0.5;
	vec2 p10 = iuv + vec2(h1.x, h0.y) - 0.5;
	vec2 p01 = iuv + vec2(h0.x, h1.y) - 0.5;
	vec2 p11 = iuv + h1 - 0.5;

    return (textureLod(tex, p00 * tsc, 0.0) * g0.x  +
            textureLod(tex, p10 * tsc, 0.0) * g1.x) * g0.y +
           (textureLod(tex, p01 * tsc, 0.0) * g0.x  +
            textureLod(tex, p11 * tsc, 0.0) * g1.x) * g1.y;
}

float hmax(vec4 x) {
    x.xy = max(x.xy, x.zw);
    return max(x.x, x.y);
}

float hsmoothmax(vec4 x) {
    float k = 3.0;
    return log(dot(exp(x * k), vec4(1))) / k;
}

void mainImage(out vec4 fragColour, in vec2 fragCoord)
{
    // Unlike the other buffers, here we want to fill the whole
    // output, so we'll be upscaling the feedback buffer.
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x + 0.5;
    // dimension fixup, because we only use a sub-rectangle of each
    // texture in the pipeline, but want to address that
    // sub-rectangle as [0,1)
    vec2 tsc = dimension / iChannelResolution[0].xy;

    vec4 colour = texbicubic(iChannel0, uv, tsc);
    vec4 threshold = texture(iChannel1, 1.0 - 0.5 / iChannelResolution[1].xy);
    vec4 meniscus = colour - threshold;
    colour = aastep(threshold, colour);

    bool lshift = texelFetch(iChannel3, ivec2(16,0),0).x >= 0.5;
    if (lshift) {
#if 1
        tsc = dimension / iChannelResolution[1].xy;
        colour = texture(iChannel1, uv * tsc);
#else
        tsc = dimension / iChannelResolution[2].xy;
        colour = texcube(iChannel2, 0, uv * tsc);
#endif
        if (uv.x >= 0.8) {
            vec4 fullness = texcubeLod(iChannel2, 0, vec2(0.5), 99.9) * mipmapfix;
            float y = fragCoord.y / iResolution.y;
            vec4 l = abs(uv.x * 20.0 - 16.5 - vec4(0.0, 1.0, 2.0, 3.0));
            vec4 ll = 1.0 - aastep(0.05, l);
            l = 1.0 - aastep(0.45, l);
            colour = aastep(y, threshold) * l;
            colour.yzwx += 
                    (aastep(y, max(fullness, setpoint))
                   - aastep(y, min(fullness, setpoint))) * ll;
        }
    }
    float alpha = hmax(colour);
    colour = pal(colour, uv);

#if 1
    // shade the meniscus a little...
    const float thinness = 4.0;
    float meni = smoothstep(-1.0, 1.0, hsmoothmax(meniscus * thinness)) * 2.0 - 1.0;
    vec4 shine = vec4(cos(atan(dFdy(meni), dFdx(meni)) + 1.2) * 0.5 + 0.5);
    colour = mix(colour, shine, alpha * (1.0 - meni) * 0.75);
#endif
    fragColour = colour;
}

