// Length of one side of the feedback buffer (square).  The rest of
// each buffer is left unused, but still has to be zeroed for
// mipmaps and interpolation.
const float dimension = 128.0;

// Volume of each blob colour to aim for.
const vec4 setpoint = vec4(0.15);

// since we don't write the whole buffer, the deepest mipmap will be
// averaged over the valid data plus a whole lot of black.  This is
// the ratio needed to compensate for that.
const float mipmapfix = 1024.0 * 1024.0 / (dimension * dimension);

// Number of samples to read for a gaussian blur.  This is done in
// two passes and the buffer is reduced resolution, so it's not a
// large cost, but we also don't need many taps.
const int blur_taps = 7;
// Strength of gaussian blur curve.
const vec4 blur_radius = vec4(0.03, 0.08, 0.05, 0.13) * 2.0;

const float tau = 2.0 * 3.14159265358979;

// Sample from one face of a cubemap.
vec4 texcubeLod(samplerCube tex, int face, vec2 uv, float lod) {
    uv = 2.0 * uv - 1.0;
    switch (face) {
    case 0: return textureLod(tex, vec3( uv.x,-uv.y,  1.0), lod);
    case 1: return textureLod(tex, vec3(-uv.x,-uv.y, -1.0), lod);
    case 2: return textureLod(tex, vec3( uv.x,  1.0, uv.y), lod);
    case 3: return textureLod(tex, vec3( uv.x, -1.0,-uv.y), lod);
    case 4: return textureLod(tex, vec3(  1.0,-uv.y,-uv.x), lod);
    case 5: return textureLod(tex, vec3( -1.0,-uv.y, uv.x), lod);
    }
}

vec4 texcube(samplerCube tex, int face, vec2 uv) {
    return texcubeLod(tex, face, uv, 0.0);
}

vec2 dimclamp(vec2 uv) {
    // incoming coordinate should be [0,1) representing coordinates
    // in the first `dimension` pixels of the image.  We need to
    // clamp these to the centre of the pixels at the two extremes
    // in order to avoid interpolating into out-of-bounds data.
    const float lo = 0.5 / dimension;
    const float hi = 1.0 - lo;
    return clamp(uv, lo, hi);
}

vec4 pdf(vec4 x) {
#if 0
    // pdf for s=0.1667: `sum(uniform(-.5,.5, 3)/3)`
    return 2.39365 * exp(-18. * x * x);
#else
    // pdf for s=0.144: `sum(uniform(-.5,.5, 4)/4)`
    return 2.77043 * exp(-24.1126 * x * x);
#endif
}

vec4 texblur(sampler2D tex, vec2 uv, vec2 v, vec2 tsc) {
    const float lod = 0.0;
    vec4 sum = vec4(0);
    vec4 den = vec4(0);

    for (int i=-blur_taps; i <= blur_taps; i++) {
        float f = float(i) / dimension;
        vec4 w = pdf(f / blur_radius);
        vec2 ofs = v * f;
        sum += textureLod(tex, dimclamp(uv + ofs) * tsc, lod) * w;
        den += w;
    }
    return sum / den;
}

