// Threshold the blurred buffer into a square POT texture (for more
// predictable mipmapping), so that we can use the mipmap as an area
// estimate.
//
// We should probably do several passes, here, with different biases
// on the threshold to see how far off the true value we are.
//

// See which face index we're updating.
int face(vec3 dir) {
    vec3 absdir = abs(dir);
    vec3 gt = vec3(greaterThan(absdir, max(absdir.yzx, absdir.zxy)));
    return int(dot(vec3(4, 2, 0), gt)) + int(dot(dir, gt) < 0.0);
}

void mainCubemap(out vec4 fragColour, in vec2 fragCoord, in vec3 rayOri, in vec3 rayDir) {
    vec2 uv = fragCoord / dimension;
#if 1 // WINDOWS
    fragColour = vec4(0);
#else
    if (iFrame == 0) fragColour = vec4(0);
#endif
    if (face(rayDir) != 0 || uv != fract(uv)) return;
    vec2 tsc = dimension / iChannelResolution[0].xy;
    vec4 colour = texture(iChannel0, uv * tsc);
    vec4 threshold = texture(iChannel1, 1.0 - 0.5 / iChannelResolution[0].xy);

    colour = smoothstep(-0.001, 0.001, colour - threshold);

    fragColour = colour;
}

