// Generic feedback buffer with swirly patterns.
//
// We use all four channels to represent four slices of 'liquid'
// which don't interact (much).  We use two stirring operations
// (`swish0()` and `swish1()`) and share these between the four
// layers with constantly-changing weights (`spinners`) between
// the two.  This has dual effects of pushing several liquids in
// the same direction sometimes, and halves the number of texture
// fetches which would be required if every liquid had
// independent flow.
//
// For a bit more complexity, where liquids overlap they squeeze
// each other a little bit by (`decay()`).

// A straight, blurred boundary which rotates around an axis which
// itself circles the centre.  Separate parameters for each plane.
vec4 spinners(vec2 uv, float t) {
    uv -= 0.5;
    vec4 ta = t * vec4(-113, 123, -133, 143) * 0.0009 * tau;
    vec4 tb = t * vec4(-177, -137, 157, 127) * 0.0004 * tau;
    vec4 cx = uv.x + cos(ta) * 0.3;
    vec4 cy = uv.y - sin(ta) * 0.3;
    vec4 th = mod(atan(cy, cx) + tb + tau, tau);
    vec4 r = sqrt(cx * cx + cy * cy);
    return smoothstep(-0.4, 0.4, (cos(th) - sin(th)) * r);
}

// Shove things around a bit to make swirly patterns
vec2 swish0(vec2 uv, float t) {
    uv += vec2(sin(t + uv.y * 16.1), cos(uv.x * 17.1)) * 0.0015;

    vec2 o = vec2(sin(t * 0.567), cos(t * -0.521)) * 0.3 + 0.5;
    const float c = cos(0.0019), s = sin(0.0019);
    uv = (uv - o) * mat2(c, s, -s, c) * 0.9996 + o;

    return uv;
}

// Shove things around a bit, but slightly differently
vec2 swish1(vec2 uv, float t) {
    uv += vec2(sin(t + uv.y * 13.1), cos(uv.x * 14.1)) * 0.0019;

    vec2 o = vec2(sin(t * 0.367), cos(t * -0.321)) * 0.3 + 0.5;
    const float c = cos(0.0015), s = -sin(0.0015);
    uv = (uv - o) * mat2(c, s, -s, c) * 0.9996 + o;

    return uv;
}

// When reading from outside the feedback buffer, fall back on a
// rotating pattern so that there's always something present to
// seed the buffer to be non-empty.
vec4 texborder(sampler2D tex, vec2 uv, float t, vec2 tsc) {
    if (uv == dimclamp(uv)) return texture(tex, uv * tsc);
    uv -= 0.5;
    vec4 th = atan(uv.y, uv.x) * 9.0 + t * vec4(0.8, 0.7, 0.9, 0.5);
    return max(vec4(0), cos(th) * 0.4 - 0.1);
}

vec4 texborder(samplerCube tex, int face, vec2 uv, float t, vec2 tsc) {
    if (uv == dimclamp(uv)) return texcube(tex, face, uv * tsc);
    uv -= 0.5;
    vec4 th = atan(uv.y, uv.x) * 9.0 + t * vec4(2.8, 1.7, 2.9, 3.5);
    return max(vec4(0), cos(th) * 0.4 - 0.1);
}

// the main feebback operation.
vec4 feedback(sampler2D tex, vec2 uv, float t, vec2 tsc) {
    return mix(texborder(tex, swish0(uv, t), t, tsc),
               texborder(tex, swish1(uv, t), t, tsc),
               spinners(uv, t));
}

vec4 feedback(samplerCube tex, int face, vec2 uv, float t, vec2 tsc) {
    return mix(texborder(tex, face, swish0(uv, t), t, tsc),
               texborder(tex, face, swish1(uv, t), t, tsc),
               spinners(uv, t));
}

// Decay where there's overlap.
vec4 repel(vec4 c) {
    return 1.0 - (c.yzwx + c.zwxy) * 0.019 - c.wxyz * 0.015;
}


vec4 updateThreshold() {
    if (iFrame == 0) return vec4(0.05);

    vec4 fullness = texcubeLod(iChannel1, 0, vec2(0.5), 99.9) * mipmapfix;
    // TODO: use more faces of iChannel1 to get a better estimate
    // of where the threshold should be.

    vec4 incr = clamp(fullness - setpoint, -0.2, 0.2);

    vec4 threshold = texture(iChannel0, 1.0 - 0.5 / iChannelResolution[0].xy);
    threshold = mix(threshold, threshold + incr, 0.02);
    threshold = clamp(threshold, 0.001, 0.999);
    return threshold;
}

// A generic feedback buffer implementation.
void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    vec2 uv = fragCoord / dimension;
    if (iFrame == 0) fragColour = vec4(0);

    // Maintain a filtered copy of the threshold in an unused
    // corner of buffer A:
    if (all(greaterThanEqual(fragCoord, iResolution.xy - 1.0))) {
        fragColour = updateThreshold();
    }
    if (uv != fract(uv)) return;

    vec2 tsc0 = dimension / iChannelResolution[0].xy;
    vec2 tsc1 = dimension / iChannelResolution[1].xy;

    vec4 colour = feedback(iChannel0, uv, iTime, tsc0);
    vec4 quantised = feedback(iChannel1, 0, uv, iTime, tsc1);

    // Mix in a little feedback from the thresholded version to
    // encourage the buffer to become more saturated.
    colour = mix(colour, quantised, 0.035);

    // A little interaction between layers.
    colour *= repel(colour);

    fragColour = colour;    
}

