    for (int i=0; i<7; i++)
    {
        p=abs(p)/clamp(p.x*p.y,.25,2.)-1.;
        if(i>0)ot=min(ot,abs(p.x)+.7*fract(abs(p.y)*.05+t*.05+float(i)*.3));

    }
    ot=exp(-10.*ot);
    return ot;
}

float box(vec3 p, vec3 l)
{
    vec3 c=abs(p)-l;
    return length(max(vec3(0.),c))+min(0.,max(c.x,max(c.y,c.z)));
}

float de(vec3 p)
{
    boxhit=0.;
    vec3 p2=p-adv;
    p2.xz*=rot(t*.3);
    p2.xy*=rot(t*.1);
    p2.yz*=rot(t*.15);
    float b=box(p2,vec3(1.));
    p.xy-=path(p.z).xy;
    float s=sign(p.y);
    p.y=-abs(p.y)-3.;
    p.z=mod(p.z,20.)-10.;
    for (int i=0; i<5; i++)
    {
        p=abs(p)-1.;
        p.xz*=rot(radians(s*-45.));
        p.yz*=rot(radians(90.));
    }
    float f=-box(p,vec3(5.,5.,10.));
    float d=min(f,b);
    if (d==b) boxp=p2, boxhit=1.;
    return d*.7;
}


vec3 march(vec3 from, vec3 dir)
{
    vec3 p,n,g=vec3(0.);
    float d, td=0.;
    for (int i=0; i<60; i++)
    {
        p=from+td*dir;
        d=de(p)*(1.-hash(gl_FragCoord.xy+t)*.3);
        if (d<det && boxhit<.5) break;
        td+=max(det,abs(d));
        float f=fractal(p.xy)+fractal(p.xz)+fractal(p.yz);
        //boxp*=.5;
        float b=fractal(boxp.xy)+fractal(boxp.xz)+fractal(boxp.yz);
        vec3 colf=vec3(f*f,f,f*f*f);
        vec3 colb=vec3(b*2.+.1,b*.1+.02,b*b*.2+.01);
        g+=colf/(3.+d*d*2.)*exp(-.0015*td*td)*step(5.,td)/2.*(1.-boxhit);
        g+=colb/(10.+d*d*20.)*boxhit*.5;
    }
    return g;
}

mat3 lookat(vec3 dir, vec3 up) 
{
	dir=normalize(dir);vec3 rt=normalize(cross(dir,normalize(up)));
    return mat3(rt,cross(rt,dir),dir);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    t=iTime*7.;
    vec3 from=path(t);
    adv=path(t+6.+sin(t*.1)*3.);
    vec3 dir=normalize(vec3(uv,.7));
    dir=lookat(adv-from,vec3(0.,1.,0.))*dir;
    vec3 col=march(from, dir);
    fragColor=vec4(col,1.0);
}

#define minSize 3.0
#define maxSize 20.0


float superLogicSnake(vec2 pos, float size){
    float center = (size-1.0)*0.5;
    float ring =  min(center-abs(pos.x-center),center-abs(pos.y-center));
    if(pos.x == (size-ring-1.0) || pos.y == ring){
        return 4.0*ring*(size-ring)+pos.x+pos.y-2.0*ring;
    }
    else {
        return 4.0*(ring+1.0)*(size-(ring+1.0))-(pos.x+pos.y-2.0*ring);
    }  
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 size_rate_time = fetchData(iChannel0, COMMON_SIZE_RATE_TIME).xyz;

    fragCoord.x = fragCoord.x -0.5*(iResolution.x-iResolution.y);
    vec2 relMouse = iMouse.xy;
    relMouse.x = relMouse.x - 0.5*(iResolution.x-iResolution.y);
    vec2 uv = fragCoord/iResolution.y;

    float xMask = 1.0-abs(floor(uv.x));
    relMouse = relMouse/iResolution.y;
    relMouse *= xMask;
    vec3 col = vec3(0.0);
    float size = floor(minSize + size_rate_time.x*(maxSize-minSize));
    float area = size*size;
    float rate = 1.6;
    vec2 mosPos = floor(relMouse.xy*size);
    float mousesnake = -superLogicSnake(mosPos,size)-1.0;
    if( uv.x < 0.0){
        float pct = 1.0 - abs(uv.y-size_rate_time.x)*50.0;

        col = vec3(.6*pct,1.0*pct,0.0);
    }
    else if(uv.x > 1.0){
        float pct = 1.0 - abs(uv.y-size_rate_time.y)*50.0;

        col = vec3(0.75*pct,0.6*pct,0.0);
    }
    else{
        vec2 tilePos = floor(uv*size);
        float snake = superLogicSnake(tilePos,size);
        float time = floor(size_rate_time.z*rate*size);
        snake = mod(snake-time+mousesnake, area)/(area-1.0);
        col = vec3(pal(snake, vec3(-0.672, 0.728, 0.098),vec3(1.418, -0.381, 0.198),
                              vec3(1.568, 2.588, 1.000), vec3(0.008, 0.838, 0.667)));
    }
    fragColor = vec4(col,1.0);
}

