//#define RETRO

vec3 nonlocalDenoise(vec2 fragTexCoord, sampler2D img, float sigma, int patchSize) {
    vec2 texelSize = 1.0 / vec2(textureSize(img, 0));

    vec3 centralColor = texture(img, fragTexCoord).rgb;
    vec3 denoisedColor = vec3(0.0);

    float totalWeight = 0.0;

    for (int i = -patchSize; i <= patchSize; i++) {
        for (int j = -patchSize; j <= patchSize; j++) {
            vec2 offset = vec2(float(i), float(j)) * texelSize;
            vec3 neighborColor = texture(img, fragTexCoord + offset).rgb;

            // Compute the similarity between centralColor and neighborColor
            float similarity = exp(-dot(centralColor - neighborColor, centralColor - neighborColor) / (2.0 * sigma * sigma));

            denoisedColor += similarity * neighborColor;
            totalWeight += similarity;
        }
    }

    denoisedColor /= totalWeight;

    return denoisedColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
