float intersect_sphere(Sphere obj, Ray r) {
    vec3 oc = r.origin - obj.center;
    if (length(oc) < obj.radius) return -1.0;
    float a = dot(r.direction, r.direction);
    float half_b = dot(oc, r.direction);
    float c = dot(oc, oc) - obj.radius*obj.radius;
    float discriminant = half_b*half_b - a*c;

    if (discriminant < 0.0) {
        return -1.0;
    } else {
        return (-half_b - sqrt(discriminant)) / a;
    }
}

RayHit scene_intersect(Ray ray) {
    float min_dist = float(1.0e38);

    Sphere obj;
    Sphere closest;
    float dist;
    for (uint i = uint(0); i < uint(SCENE_SIZE); ++i) {
        obj = SCENE[i];
        dist = intersect_sphere(obj, ray);

        if (dist > 0.0 && dist < min_dist) {
            min_dist = dist;
            closest = obj;
        }
    }
    if (min_dist == float(1.0e38)) return RAY_MISS;

    vec3 position =  ray.origin + ray.direction * min_dist;
    RayHit ret = RayHit( true, position, normalize(position - closest.center), closest );

    return ret;
}

vec3 v_lerp(vec3 v1, vec3 v2, float t) {
    return (v2 - v1) * t + v1;
}

vec3 rand_hemis_dir(vec3 dir) {
    vec3 rand_dir = normalize(vec3(rand(dir * float(++state)), rand(dir * float(++state)), rand(dir * float(++state))));
    return rand_dir * (sign(dot(dir, rand_dir)) * 2.0 - 1.0);
}

void init_state(float time) {
    state = time;
}

float clip(float x, float a, float b) {
    if (x < a) return a;
    if (x > b) return b;
               return x;
}

#define SUN vec3(1.0, 0.7, 1.0)

vec3 sky_color(vec3 direction) {
    vec3 sky_blue = vec3(0.380, 0.769, 0.961);

    float sun = pow(clamp(dot(direction, normalize(SUN)), 0.0, 1.0), 500.0) * 100.0;
    vec3 sunCol = vec3(sun, sun * 0.9, 0.0);

    vec3 ret = sky_blue + sunCol;

    return ret;
}


vec3 ray_shader(Ray ray, uint x, uint y) {
    vec3 color = vec3(1.0);
    vec3 light = vec3(0.0);

    for (int bounce = 0; bounce < MAX_BOUNCES; ++bounce) {
        RayHit intersect = scene_intersect(ray);
        if (!intersect.hit) {
            vec3 sky = sky_color(ray.direction);
            light = light + sky * color;
            break;
        };

        Material mat;
        mat = intersect.object.mat;

        light = light + mat.emission * color;
        color = color * mat.color;

        ray.origin = intersect.position + intersect.normal * 0.001;
        ray.direction = v_lerp(reflect(ray.direction, intersect.normal), normalize(intersect.normal + normalize(vec3(rand(float(++state)), rand(float(++state)), rand(float(++state))))), mat.diffuse);
    }
    return light;
}

