vec3 flim_transform(sampler2D mc, vec3 i, float e){
    i = max(i, 0.);
    i *= pow(2., pe + e);
    i = min(i, 5000.);
    mat3 em, emi;
    if (!flm(mc, em, emi)){ return vec3(1, 0, 1); }
    vec3 bex = pb * em;
    const float big = 10000000.0;
    vec3 white_cap = nap(vec3(big, big, big), bex);
    i = lerp(i, i * fpff, fpffs);
    i *= em;
    i = nap(i, bex);
    i *= emi;
    i = max(i, 0.);
    i /= white_cap;
    if (bp == -1.){
        vec3 black_cap = nap(vec3(0.), bex);
        black_cap /= white_cap;
        i = uo(i, rgb_avg(black_cap) * 1000., 0.);
    }else{ i = uo(i, bp, 0.); }
    i = lerp(i, i * pff, pffs);
    i = clamp(i, 0., 1.);
    float mono = rgb_avg(i);
    float mixFac = (mono < 0.5) ? mrc(mono, 0.05, 0.5, 0., 1.) : mrc(mono, 0.5, 0.95, 1., 0.);
    i = lerp(i, bhs(i, 0.5, ms, 1.), mixFac);
    i = clamp(i, 0., 1.);
    i = pow(i, vec3(1./ 2.2));
    return i; }

