#define AA  0   // (= 0/1) optional antialiasing

#define VAR_ZERO min (nFrame, 0)

float PrBox2Df (vec2 p, vec2 b);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float PrCaps2Df (vec2 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);

vec3 sunDir, qHit, lBase;
vec2 cId;
float tCur, dstFar, bGrid, grLim, wlkSpd, wlkScl, vAxHt, chnEl;
int nFrame, idObj;
const int idBas = 1, idLnkT = 2, idLnk = 3, idAx = 4, idWhl = 5, idVane = 6, idStruc = 7,
   idGrnd = 8, idSprk = 11, idPin = 12, idPlt = 13;
const float s_a = 38.0, s_b = 41.5, s_c = 39.3, s_d = 40.1, s_e = 55.8, s_f = 39.4,
   s_g = 36.7, s_h = 65.7, s_i = 49.0, s_j = 50.0, s_k = 61.9, s_l = 7.8, s_m = 15.0;
const float pi = 3.1415927;

#define CosSin(x) (sin ((x) + vec2 (0.5 * pi, 0.)))
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

struct Leg {
  vec2 v[8], cs[10], cswAng;
  float wAng;
};
struct Walker {
  Leg leg[2];
  vec2 csVane;
  float szFac;
};
Walker wlk;

#define ACOSR(x, y, z) acos (((x) * (x) + (y) * (y) - (z) * (z)) / (2. * (x) * (y)))
#define ATANV(v) atan ((v).y, (v).x)

void ObjState ()
{ //  (Leg from the Strandbeest: see https://en.wikipedia.org/wiki/Jansen's_linkage)
  float a[10], aa, g, s, t;
  wlk.szFac = wlkScl / (1. + 0.3 * Maxv2 (abs (cId)));
  t = tCur * wlkScl / wlk.szFac;
  wlk.leg[0].wAng = - wlkSpd * t;
  wlk.leg[1].wAng = wlk.leg[0].wAng + pi;
  for (int k = 0; k < 2; k ++) {
    wlk.leg[k].v[0] = vec2 (0., 0.);
    wlk.leg[k].v[1] = wlk.leg[k].v[0] + vec2 (s_a, s_l);
    wlk.leg[k].v[2] = wlk.leg[k].v[1] + Rot2D (vec2 (s_m, 0.), wlk.leg[k].wAng);
    aa = ATANV (wlk.leg[k].v[2] - wlk.leg[k].v[0]);
    s = length (wlk.leg[k].v[2] - wlk.leg[k].v[0]);
    a[0] = aa + ACOSR (s, s_b, s_j);
    wlk.leg[k].v[3] = wlk.leg[k].v[0] + Rot2D (vec2 (s_b, 0.), a[0]);
    a[1] = aa - ACOSR (s, s_c, s_k);
    wlk.leg[k].v[4] = wlk.leg[k].v[0] + Rot2D (vec2 (s_c, 0.), a[1]);
    a[2] = ACOSR (s_b, s_d, s_e) + a[0];
    wlk.leg[k].v[5] = wlk.leg[k].v[0] + Rot2D (vec2 (s_d, 0.), a[2]);
    s = length (wlk.leg[k].v[5] - wlk.leg[k].v[4]);
    g = ACOSR (s, s_c, s_d) + ACOSR (s, s_g, s_f) + pi + a[1];
    wlk.leg[k].v[6] = wlk.leg[k].v[4] + Rot2D (vec2 (s_g, 0.), g);
    wlk.leg[k].v[7] = wlk.leg[k].v[4] + Rot2D (vec2 (s_i, 0.), g + ACOSR (s_g, s_i, s_h));
    a[3] = ACOSR (s_d, s_e, s_b) + a[2] - pi;
    a[4] = ATANV (wlk.leg[k].v[4] - wlk.leg[k].v[6]);
    a[5] = ATANV (wlk.leg[k].v[5] - wlk.leg[k].v[6]);
    a[6] = ATANV (wlk.leg[k].v[7] - wlk.leg[k].v[6]);
    a[7] = ATANV (wlk.leg[k].v[7] - wlk.leg[k].v[4]);
    a[8] = ATANV (wlk.leg[k].v[3] - wlk.leg[k].v[2]);
    a[9] = ATANV (wlk.leg[k].v[4] - wlk.leg[k].v[2]);
    for (int m = 0; m < 10; m ++) wlk.leg[k].cs[m] = CosSin (- a[m]);
    wlk.leg[k].cswAng = CosSin (- wlk.leg[k].wAng);
  }
  wlk.csVane = CosSin (4. * t);
  chnEl = atan (vAxHt - s_l, - lBase.x);
}

void LinkDf (vec3 p, vec2 v, vec2 cs, float l, int id, inout float dMin)
{
  vec3 q;
  float d;
  q = p;
  q.xy = Rot2Cs (q.xy - v, cs);
  d = max (PrCaps2Df (q.yx - vec2 (0., 0.5 * l), 2., 0.5 * l), abs (q.z) - 0.5);
  DMINQ (id);
}

float ChainDf (vec3 p, float aRot, float wgFac, float dMin)
{
  vec3 q, sp;
  float d, wlRad, cRad, cLen, nTh, nSp, wlThk, lnkLen, lnkWid, lnkThk, wlGap, bx, by, ds, a, r;
  wlRad = 1.;
  cRad = 0.04;
  nTh = 32.;
  nSp = 6.;
  wlThk = 0.05;
  lnkLen = 2. * pi * wlRad / nTh;
  lnkWid = 1.4 * cRad;
  lnkThk = 0.15 * wlThk;
  wlGap = wgFac * lnkLen;
  cLen = 1.9 * wlThk;
  sp.xz = sign (p.xz);
  bx = wlGap - abs (p.x);
  q = p;
  q.x = abs (q.x) - wlGap;
  q.xz = Rot2D (q.xz, aRot * sp.x);
  r = length (q.xz);
  a = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  d = min (max (abs (r - 0.96 * wlRad) - 0.09 * wlRad, abs (q.y) - wlThk),
     max (r - 0.25 * wlRad, abs (q.y) - 1.5 * wlThk));
  d = min (d, max (PrBox2Df (vec2 (dot (q.xz, sin (2. * pi * floor (nSp * a + 0.5) / nSp +
     vec2 (0., 0.5 * pi))), q.y), vec2 (0.05, wlThk)), r - wlRad));
  q.xz = Rot2D (q.xz, 2. * pi * floor (nTh * a + 0.5) / nTh);
  d = SmoothMax (d, - PrCaps2Df (vec2 (q.z, q.x + wlRad + 0.1), 0.045, 0.08), 0.01);
  DMINQ (idSprk);
  q.x += wlRad;
  d = max (PrCylDf (q.xzy, cRad, cLen), bx);
  DMINQ (idPin);
  q = p;
  q.xz -= vec2 (aRot * wlRad, wlRad) * sp.z;
  q.x = mod (q.x + 0.5 * lnkLen, lnkLen) - 0.5 * lnkLen;
  d = max (PrCylDf (q.xzy, cRad, cLen), - bx);
  DMINQ (idPin);
  for (int k = VAR_ZERO; k < 2; k ++) {
    by = (1.3 + 0.3 * float (k)) * wlThk;
    ds = 0.5 + float (k);
    q = p;
    q.xz -= vec2 (aRot * wlRad + ds * lnkLen, wlRad) * sp.z;
    q.x = mod (q.x + lnkLen, 2. * lnkLen) - lnkLen;
    d = max (SmoothMax (PrCaps2Df (q.zx, lnkWid, 0.5 * lnkLen), abs (abs (q.y) - by) - lnkThk, 0.002), - bx);
    DMINQ (idPlt);
    q = p;
    q.x = abs (q.x) - wlGap;
    q.xz = Rot2D (q.xz, (aRot + ds * 2. * pi / nTh) * sp.x);
    q.xz = Rot2D (q.xz, 2. * pi * floor (0.5 * nTh * atan (q.z, - q.x) / (2. * pi) + 0.5) / (0.5 * nTh));
    q.x += wlRad;
    d = max (SmoothMax (PrCaps2Df (q.xz, lnkWid, 0.5 * lnkLen), abs (abs (q.y) - by) - lnkThk, 0.002), bx);
    DMINQ (idPlt);
  }
  return dMin;
}

float ObjDf (vec3 p)
{ // (Based on "Wind Walker")
  vec3 q, ps;
  float dMin, d, sx, chnSzFac;
  int kx, kz;
  p.xz -= bGrid * (cId + 0.5);
  dMin = dstFar / wlk.szFac;
  p /= wlk.szFac;
  p.y -= lBase.y;
  p.xz = Rot2Cs (p.xz, CosSin (0.25 * pi));
  p.xz = vec2 (p.z, - p.x);
  for (int k = VAR_ZERO; k < 4; k ++) {
    kx = k / 2;
    kz = k - 2 * kx;
    sx = sign (float (kx) - 0.5);
    ps = p;
    ps.z *= (sign (float (kz) - 0.5)) * sx;
    ps.xz += vec2 (lBase.x * sx, lBase.z);
    q = ps;
    q.z -= 4.;
    q.xy = Rot2Cs (q.xy - wlk.leg[kz].v[1], wlk.leg[kz].cswAng);
    d = PrCylAnDf (q, s_m, 2., 1.);
    q.xy = (abs (q.x) > abs (q.y)) ? q.xy : q.yx;
    d = min (d, max (PrBox2Df (q.xy, vec2 (s_m, 1.8)), abs (q.z) - 0.8));
    DMINQ (idWhl);
    for (int j = VAR_ZERO + 2; j <= 7; j ++) {
      q = ps - vec3 (wlk.leg[kz].v[j], 0.2);
      d = PrCylDf (q, ((j < 7) ? 1.5 : 2.5), ((j == 2) ? 5. : 3.));
      DMINQ (idAx);
    }
    LinkDf (ps, wlk.leg[kz].v[0], wlk.leg[kz].cs[0], s_b, idLnkT, dMin);
    LinkDf (ps, wlk.leg[kz].v[0], wlk.leg[kz].cs[2], s_d, idLnkT, dMin);
    LinkDf (ps, wlk.leg[kz].v[5], wlk.leg[kz].cs[3], s_e, idLnkT, dMin);
    LinkDf (ps, wlk.leg[kz].v[6], wlk.leg[kz].cs[4], s_g, idLnkT, dMin);
    LinkDf (ps, wlk.leg[kz].v[6], wlk.leg[kz].cs[6], s_h, idLnkT, dMin);
    LinkDf (ps, wlk.leg[kz].v[4], wlk.leg[kz].cs[7], s_i, idLnkT, dMin);
    ps.z -= 1.4;
    LinkDf (ps, wlk.leg[kz].v[0], wlk.leg[kz].cs[1], s_c, idLnk, dMin);
    LinkDf (ps, wlk.leg[kz].v[6], wlk.leg[kz].cs[5], s_f, idLnk, dMin);
    LinkDf (ps, wlk.leg[kz].v[2], wlk.leg[kz].cs[8], s_j, idLnk, dMin);
    ps.z += 2.8;
    LinkDf (ps, wlk.leg[kz].v[2], wlk.leg[kz].cs[9], s_k, idLnk, dMin);
  }
  q = p;
  q.x -= 20.;
  d = max (PrCaps2Df (vec2 (q.y, q.x - 5.), 2.5, lBase.x + 40.), abs (q.z) - lBase.z + 7.);
  d = max (d, - PrBox2Df (vec2 (abs (q.x + 20. - s_a) - lBase.x, q.z), vec2 (17., 11.)));
  d = max (d, 3. - length (vec2 (q.y + 5., abs (q.z) - 8.)));
