  q = p;
  q.x = abs (q.x) - lBase.x;
  d = PrCylDf (q, 1.5, lBase.z + 2.);
  DMINQ (idAx);
  q = p;
  q.xy -= vec2 (s_a, s_l);
  q.x = abs (q.x) - lBase.x;
  d = PrCylDf (q, 1.5, lBase.z - 2.);
  DMINQ (idAx);
  q = p;
  q.xy -= vec2 (s_a, s_l - 1.5);
  d = max (PrCaps2Df (q.xy, 4.5, 2.), abs (q.z) - lBase.z + 7.);
  DMINQ (idStruc);
  q.x = abs (q.x) - lBase.x;
  d = max (PrCaps2Df (q.xy, 4.5, 2.), abs (abs (q.z) - 0.55 * lBase.z) - 0.25 * lBase.z);
  DMINQ (idStruc);
  q = p;
  q.z = abs (abs (q.z) - 27.5);
  q -= vec3 (s_a, 32., 24.);
  d = max (PrCaps2Df (q.xy, 4., 28.), abs (q.z) - 1.5);
  DMINQ (idStruc);
  q = p;
  q.xy -= vec2 (s_a, vAxHt);
  d = PrCylDf (q, 2.5, lBase.z - 5.);
  DMINQ (idAx);
  q.xy = Rot2Cs (q.xy, wlk.csVane);
  d = max (abs (length (q.xy - vec2 (18., 10.)) - 20.) - 0.2, q.y);
  q.xy = Rot2Cs (q.xy, CosSin (2. * pi / 3.));
  d = min (d, max (abs (length (q.xy - vec2 (18., 10.)) - 20.) - 0.2, q.y));
  q.xy = Rot2Cs (q.xy, CosSin (2. * pi / 3.));
  d = min (d, max (abs (length (q.xy - vec2 (18., 10.)) - 20.) - 0.2, q.y));
  q.z = abs (q.z) - 27.5;
  d = max (d, abs (q.z) - 21.);
  DMINQ (idVane);
  chnSzFac = 12.76;
  q = p;
  q.z = abs (q.z);
  q -= vec3 (s_a, s_l, 8.);
  dMin = chnSzFac * ChainDf (vec3 (q.xy, - q.z).xzy / chnSzFac, - wlk.leg[0].wAng,
     24., dMin / chnSzFac);
  q = p;
  q.xy -= vec2 (s_a - 0.5 * lBase.x, 0.5 * (vAxHt + s_l));
  dMin = chnSzFac * ChainDf (vec3 (Rot2D (q.xy, chnEl), - q.z).xzy / chnSzFac,
     - wlk.leg[0].wAng - chnEl, 16., dMin / chnSzFac);
  return wlk.szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 rdi, p;
  bool cNu;
  float dHit, d, s, sb, eps;
  if (rd.x == 0.) rd.x = 1e-4;
  if (rd.z == 0.) rd.z = 1e-4;
  rdi.xz = 1. / rd.xz;
  eps = 1e-4;
  cNu = true;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    if (cNu) {
      cId = floor (p.xz / bGrid);
      sb = Minv2 ((bGrid * (cId + step (0., rd.xz)) - ro.xz) * rdi.xz);
      ObjState ();
      cNu = false;
    }
    d = (Maxv2 (abs (cId)) <= grLim) ? ObjDf (p) : dstFar;
    s = max (dHit, sb);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      cNu = true;
    }
    if (d < eps || dHit > dstFar || p.y < 0.) break;
  }
  if (d >= eps || p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    cId = floor (p.xz / bGrid);
    if (cId != cIdP) {
      ObjState ();
      cIdP = cId;
    }
    if (Maxv2 (abs (cId)) <= grLim) {
      h = ObjDf (p);
      sh = min (sh, smoothstep (0., 0.05 * d, h));
    } else h = 0.3 * bGrid;
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.7 + 0.3 * sh;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.41, 0.55);
  } else if (rd.y < 0.) {
    col = vec3 (0.3, 0.41, 0.55);
  } else {
    q = 0.02 * (ro.xz + 0.5 * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.4, 0.5, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return 0.8 * col;
}

vec4 ObjCol ()
{
  vec4 col4;
  if (idObj == idBas) {
    col4 = vec4 (0.7, 0.8, 0.7, 0.05) * (0.9 +
       0.1 * smoothstep (0.1, 0.13, fract (8. * abs (qHit.z) / 50. + 0.5)));
  } else if (idObj == idStruc) {
    col4 = vec4 (0.8, 0.9, 0.8, 0.05);
  } else if (idObj == idLnkT) {
    col4 = vec4 (0.85, 0.85, 0.9, 0.1) * (0.8 + 0.2 * smoothstep (0.18, 0.22, abs (qHit.y)));
  } else if (idObj == idLnk) {
    col4 = vec4 (0.95, 0.95, 1., 0.1) * (0.8 + 0.2 * smoothstep (0.18, 0.22,
       abs (abs (qHit.y) - 0.8)));
  } else if (idObj == idAx) {
    col4 = vec4 (0.8, 0.7, 0.2, 0.1);
  } else if (idObj == idWhl) {
    col4 = vec4 (0.8, 0.6, 0.1, 0.1);
  } else if (idObj == idVane) {
    col4 = mix (vec4 (1., 1., 1., 0.), vec4 (HsvToRgb (vec3 (fract (0.4 + 0.7 * Hashfv2 (cId *
       vec2 (1.5, 2.5) + 1.1)), 0.6, 1.)), 0.), smoothstep (0.25, 0.3, abs (abs (qHit.z) - 12.)));
  } else if (idObj == idSprk) {
    col4 = vec4 (0.8, 0.6, 0.1, 0.1);
  } else if (idObj == idPin) {
    col4 = vec4 (0.65, 0.7, 0.8, 0.1);
  } else if (idObj == idPlt) {
    col4 = vec4 (0.8, 0.8, 0.9, 0.1);
  }
  return col4;
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec4 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  h = RippleHt (p);
  e = vec2 (0.002, 0.);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vec4 (vn, h);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vn4;
  vec3 col, vn, q;
  float dstObj, dstGrnd, sh, t;
  bool isBg;
  wlkScl = 0.07;
  lBase = vec3 (60., 86., 60.);
  vAxHt = 60.;
  wlkSpd = 1.5;
  dstGrnd = dstFar;
  isBg = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = ObjCol ();
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    q = ro;
    q.xz += wlkSpd * tCur;
    t = 1. - smoothstep (0.5, 0.7, dstGrnd / dstFar);
    vn4 = RippleNorm (0.25 * q.xz, vec3 (0., 1., 0.), 4. * t);
    vn = vn4.xyz;
    col4 = vec4 (0.8, 0.7, 0.4, 0.) * (1. - 0.15 * t * Fbm2 (32. * q.xz)) *
       mix (1., 0.97 + 0.03 * smoothstep (0.1, 0.3, vn4.w), 2. * t);
  } else {
    col = SkyBgCol (ro, rd);
    isBg = true;
  }
  if (! isBg) {
    sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    col = col4.rgb * (0.2 + 0.2 * max (dot (vn, normalize (sunDir * vec3 (-1., 0., -1.))), 0.) +
       0.8 * sh * max (dot (vn, sunDir), 0.)) +
       step (0.95, sh) * col4.a * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
    if (dstObj >= dstFar) col = mix (col, 0.8 * vec3 (0.3, 0.41, 0.55), pow (1. + rd.y, 16.));
  }
  return clamp (col, 0., 1.);
}

#define N_WIN  1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, vd;
  vec2 canvas, uv, uvv, mMid[N_WIN], ut[N_WIN], mSize, msw;
  float el, az, azm, zmFac, asp, sr;
  int vuId, regId;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 50.;
  asp = canvas.x / canvas.y;
  mSize = (1./5.) * vec2 (asp, 1.);
  mMid[0] = (1. - mSize.y) * vec2 (asp, 1.);
  for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    for (int k = 0; k < N_WIN; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = k + 1;
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = k + 1;
      break;
    }
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  az = 0.;
  azm = 0.;
  el = (vuId == 0) ? -0.11 * pi : -0.09 * pi;
  if (mPtr.z > 0. && vuId == regId) {
    azm = 2.5 * pi * msw.x;
    az += azm;
    el += 0.5 * pi * msw.y;
  } else {
    az = mod (az + 0.01 * pi * tCur + pi, 2. * pi) - pi;
    el += 0.05 * pi * sin (0.015 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, -0.03 * pi);
  vuMat = StdVuMat (el, az);
  if (length (uv * vec2 (1. / asp, 1.)) < 1.5 * SmoothBump (0.25, 0.75, 0.01,
     fract (tCur / 60.))) vuId = 1 - vuId;
  zmFac = 4.;
  if (vuId == 0)zmFac = 2.5 * (zmFac + 4. * abs (azm));
  bGrid = 20.;
  grLim = (vuId == 0) ? 0. : 1.;
  ro = vec3 (0., 0., -100.);
  ro.y = (vuId == 0) ? 6. : 3.;
  ro = vuMat * ro;
  ro.xz += 0.5 * bGrid;
  dstFar = 300.;
  sunDir = vuMat * normalize (vec3 (0.5, 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.7, 0.3, 0.3);
  }
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (vec2 (p.x, sign (p.y) * (max (0., abs (p.y) - h)))) - r;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (mod (dot (p, vec2 (37., 39.)), 2. * pi)) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (mod (p + vec2 (0., 1.), 2. * pi)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

// Fast Torus
// by Ruud Helderman, August-September 2023 - MIT License

// A torus is such a simple mathematical object that you can do
// a 3D projection by solving a quartic or even a cubic equation.
// Pros: efficient - no 'for' loops (except for oversampling).
// Cons: complex math; less flexible than raymarching.

const float PI = 3.1415926536;

//---------------------------------------------------------------------------------------
// Torus
//---------------------------------------------------------------------------------------

// Torus, centered at (0, 0, 0), with center axis along the Z axis.
// Formula: (x^2 + y^2 + z^2 + R^2 - r^2)^2 = 4R^2(x^2 + y^2)
// (see https://en.wikipedia.org/wiki/Torus)

const float R = 3.0;   // major radius (distance between center axis and center of tube)
const float r = 2.0;   // minor radius (radius of tube)

// Given a point on the surface of the torus, get its skin coordinates.
vec2 torus_to_skin(vec3 p)
{
    return atan(p.yz, vec2(p.x, length(p.xy) - R)) / PI;
}

// Given a point on the surface of the torus, get its normal vector (normalized).
vec3 torus_normal(vec3 p)
{
    return (vec3(normalize(p.xy) * R, 0) - p) / r;
}

// Dot product of normal vector with ray from source. Used for lighting.
float torus_normal_dot(vec3 p, vec3 source)
{
    return dot(torus_normal(p), normalize(source - p));
}

//---------------------------------------------------------------------------------------
// Ray
//---------------------------------------------------------------------------------------

// Formula: y = uv.x * (x+R+r), z = uv.y * (x+R+r)

// Camera is located at surface of torus (on negative part of X axis).
// That location greatly simplifies our calculation as we already know one intersection
// between ray and torus, leaving us with only a cubic equation to solve.
const vec3 camera = vec3(-R-r, 0, 0);

// Camera looks inside (toward center axis), along X axis in positive direction.
// Given the X coordinate of a point on the ray, calculate the remaining coordinates.
vec3 ray_point(float x, vec2 uv)
{
    return vec3(x, (x - camera.x) * uv);
}

//---------------------------------------------------------------------------------------
// Solve cubic equation
//---------------------------------------------------------------------------------------

float cubic_1_root(float q, float sd)
{
    vec2 v = vec2(sd, -sd) - q;
    v = sign(v) * pow(abs(v), vec2(1.0/3.0));
    return v.x + v.y;
}

float cubic_smallest_of_3_roots(float p, float sp, float q)
{
    return -2.0 * sp * cos(acos(q / (p * sp)) / 3.0);
}

// Calculate the smallest real root of the depressed cubic equation x^3-3px+2q=0
float cubic_smallest_real_root(float p, float q)
{
    float d = q*q - p*p*p;
    return d > 0.0 ? cubic_1_root(q, sqrt(d)) : cubic_smallest_of_3_roots(p, sqrt(p), q);
}

//---------------------------------------------------------------------------------------
// Intersect ray with torus
//---------------------------------------------------------------------------------------

// Calculate coordinates of intersection between ray and torus
vec3 intersect_torus(vec2 uv)
{
    vec2 uv2 = uv*uv;
    float k = uv2.x + uv2.y;

    vec2 v = mat4x2(1, 2, -1, -5, -3, -9, -3, 18) * vec4(R*R+r*r, R*r, R*r*k, R*R*uv2.y);

    float p = 4.0/9.0*v.x;
    float q = 4.0/27.0*(R+r)*v.y;

    float x = (cubic_smallest_real_root(p, q) - (R+r)*(k-1.0/3.0)) / (k+1.0);

    return ray_point(x, uv);
}

//---------------------------------------------------------------------------------------
// Skin of torus
//---------------------------------------------------------------------------------------

const float number_of_skins = 10.0;
const float time_between_transitions = 5.0;
const float transition_duration = 0.4;
const float animation_speed = 0.03;

float transition_step(float n)
{
    return smoothstep(0.0, transition_duration / time_between_transitions, n);
}

float timeslot(float n)
{
    float m = mod(-n, number_of_skins);
    return transition_step(m) - transition_step(m - 1.0);
}

float square_wave(float x)
{
    return 0.5 + 0.5 * tanh(16.0 * cos(2.0 * PI * x));
}

float squares(vec2 uv, float n)
{
    vec2 s = tanh(n * sin(12.0 * PI * uv));
    return s.x * s.y;
}

vec3 skin_toothpaste(vec2 uv)
{
    return mix(vec3(0.2, 0.4, 1), vec3(1), square_wave(dot(uv, vec2(1, 3))));
}

vec3 skin_grid(vec2 uv)
{
    return mix(vec3(0), vec3(1, 0.3, 0.3), abs(squares(uv, 8.0)));
}

vec3 skin_morph(vec2 uv, float t)
{
    return mix(vec3(0.8, 0, 1), vec3(0.4, 0, 0.5), square_wave(length(sin(t * 0.25) + sin(8.0 * PI * uv))));
}

vec3 skin_eight(vec2 uv)
{
    return mix(vec3(0.5), vec3(1), square_wave(uv.x + uv.y));
}

vec3 skin_checkered(vec2 uv)
{
    return mix(vec3(0.8, 0.6, 0), vec3(0.6, 0.4, 0), squares(uv, 32.0));
}

vec3 skin_wired(vec2 uv)
{
    return mix(vec3(0), vec3(0.6, 0.8, 1), 0.5 + 0.5 * sin(32.0 * PI * (uv.x + uv.y)));
}

vec3 skin_channel(vec2 uv, sampler2D s)
{
    return texture(s, acos(cos(uv * PI)) / PI).rgb;
}

// Every few seconds a different skin
vec3 skin(vec2 uv, float t)
{
    uv += t * animation_speed;
    float s = t / time_between_transitions;
    //s = 2.0;        // uncomment and specify number to stick to that skin
    vec3 c = vec3(0);
    float a;
    a = timeslot(--s); if (a > 0.0) c += a * skin_toothpaste(uv);
    a = timeslot(--s); if (a > 0.0) c += a * skin_grid(uv);
    a = timeslot(--s); if (a > 0.0) c += a * skin_morph(uv, t);
    a = timeslot(--s); if (a > 0.0) c += a * skin_eight(uv);
    a = timeslot(--s); if (a > 0.0) c += a * skin_checkered(uv);
    a = timeslot(--s); if (a > 0.0) c += a * skin_wired(uv);
    a = timeslot(--s); if (a > 0.0) c += a * skin_channel(uv, iChannel0);
    a = timeslot(--s); if (a > 0.0) c += a * skin_channel(uv, iChannel1);
    a = timeslot(--s); if (a > 0.0) c += a * skin_channel(uv, iChannel2);
    a = timeslot(--s); if (a > 0.0) c += a * skin_channel(uv, iChannel3);
    return c;
}

//---------------------------------------------------------------------------------------
// Projection of torus onto 2D screen
//---------------------------------------------------------------------------------------

// Light source is at the same point as the camera;
// helps keep the implementation simple (no visible shadow of the axis on the outer wall).

const float dark = 0.3;
const float light = 1.0;

// Find a balance between horizontal and vertical resolution while maintaining aspect ratio.
float average(vec2 res)
{
    return 0.5 * (res.x + res.y);
}

// Convert from pixels to normalized centered coordinates, roughly [-1, 1]
vec2 normalize_screen(vec2 uv, vec2 res)
{
    return (2.0 * uv - res) / average(res);
}

// Get the color of the pixel at the given screen coordinates and the given time
vec3 projection(vec2 uv, float t)
{
    vec3 p = intersect_torus(normalize_screen(uv, iResolution.xy));
    return skin(torus_to_skin(p), t) * mix(dark, light, torus_normal_dot(p, camera));
}

//---------------------------------------------------------------------------------------
// Anti-aliasing
//---------------------------------------------------------------------------------------

// Easy adaptive sampling, by Fabrice Neyret
// https://shadertoyunofficial.wordpress.com/2021/03/09/advanced-tricks/

vec3 oversampled_projection(vec2 uv)
{
    vec3 color = projection(uv, iTime);
    if (fwidth(length(color)) > .01)
    {
        for (int k = 0; k < 9; k += k==3?2:1)
        {
            color += projection(uv + vec2(k%3-1,k/3-1)/3.0, iTime);
        }
        color /= 9.0;
        //color.r++;        // uncomment to see where the oversampling occurs
    }
    return color;
}

//---------------------------------------------------------------------------------------
// Main
//---------------------------------------------------------------------------------------

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(oversampled_projection(fragCoord), 1);
}

// Based on https://blog.demofox.org/2020/05/25/casual-shadertoy-path-tracing-1-basic-camera-diffuse-emissive/
// and https://blog.demofox.org/2020/06/06/casual-shadertoy-path-tracing-2-image-improvement-and-glossy
// and https://blog.demofox.org/2020/06/14/casual-shadertoy-path-tracing-3-fresnel-rough-refraction-absorption-orbit-camera/
// And yes, I am sticking with my uncreative naming scheme :)

// https://64.github.io/tonemapping/#uncharted-2
vec3 Uncharted2(in vec3 color) {
    color *= 2.0;

    float A = 0.15, B = 0.50, C = 0.10;
    float D = 0.20, E = 0.02, F = 0.30;
    color = (((A * color + C * B) * color + D * E) / ((A * color + B) * color + D * F)) - E / F;

    float whiteMax = 4.0;
    color /= (((A * whiteMax + C * B) * whiteMax + D * E) / ((A * whiteMax + B) * whiteMax + D * F)) - E / F;

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    fragColor = vec4(pow(Uncharted2(fragColor.rgb), vec3(0.4545)), 1.0);
}

