float marchEpsilon = 0.001;
float epsilonNormal = 0.001;


float dist(vec3 point)
{
    return distance(fract(point), vec3(0.5, 0.5, 0.5)) * 0.5;
}

vec3 getNormal(vec3 pos)
{
    vec3 normal;

    normal.x = dist(pos + vec3(epsilonNormal, 0.0, 0.0));
    normal.x -= dist(pos + vec3(-epsilonNormal, 0.0, 0.0));

    normal.y = dist(pos + vec3(0.0, epsilonNormal, 0.0));
    normal.y -= dist(pos + vec3(0.0, -epsilonNormal, 0.0));

    normal.z = dist(pos + vec3(0.0, 0.0, epsilonNormal));
    normal.z -= dist(pos + vec3(0.0, 0.0, -epsilonNormal));

    return normalize(normal);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int stepCount = 255;
    vec2 uv = fragCoord/iResolution.x;
    float ar = iResolution.y/iResolution.x;

    vec3 sunDir = normalize(vec3(sin(iTime * 0.1), 1.0, cos(iTime * 0.1)));

    vec3 startPos = texelFetch(iChannel0, ivec2(0, 0), 0).xyz;
    vec3 rot = texelFetch(iChannel0, ivec2(1, 0), 0).xyz;

    vec3 pos = startPos;
    vec3 dir = normalize(vec3(uv.x - 0.5, uv.y - 0.5 * ar, 1.0));


    vec3 old = dir;
    dir.y = old.y * cos(rot.y) + old.z * sin(rot.y);
    dir.z = old.z * cos(rot.y) + old.y * -sin(rot.y);
    old = dir;
    dir.x = old.x * cos(rot.x) + old.z * sin(rot.x);
    dir.z = old.z * cos(rot.x) + old.x * -sin(rot.x);





    int marchStep = 0;
    int endStepCount = 0;
    while (marchStep < stepCount)
    {
        float dist = dist(pos);


        if (dist < marchEpsilon)
        {
            endStepCount = marchStep;
            marchStep = stepCount;
        }
        pos += dir * dist;
        marchStep++;
    }
    vec3 endCol;
    if (marchStep == stepCount + 1)
    {
        vec3 col = vec3(1.0, 1.0, 1.0);
        float brightness = (0.5 + 0.5 * dot(getNormal(pos), sunDir)) * (1.0 - float(endStepCount) / float(stepCount));
        endCol = col * brightness;
    }
    else
    {
       endCol = vec3(0.5 + 0.25 * dot(dir, vec3(0.0, 1.0, 0.0)), 0.75 + 0.25 * dot(dir, vec3(0.0, 1.0, 0.0)), 1.0);
    }

    fragColor = vec4(endCol, 1.0);
}

