const int KEY_UP    = 38;
const int KEY_DOWN  = 40;
const int KEY_LEFT  = 37;
const int KEY_RIGHT = 39;

const int KEY_W = 87;
const int KEY_S = 83;
const int KEY_A = 65;
const int KEY_D = 68;
const int KEY_E = 69;
const int KEY_Q = 81;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ipos = ivec2(int(fragCoord.x), int(fragCoord.y));
    if ((ipos.x == 0 || ipos.x == 1) && ipos.y == 0)
    {
        vec3 pos = vec3(texelFetch(iChannel0, ivec2(0, 0), 0));
        vec3 rot = vec3(texelFetch(iChannel0, ivec2(1, 0), 0));

        float xAxis = texelFetch(iChannel1, ivec2(KEY_D, 0), 0).r;
        xAxis -= texelFetch(iChannel1, ivec2(KEY_A, 0), 0).r;

        float yAxis = texelFetch(iChannel1, ivec2(KEY_E, 0), 0).r;
        yAxis -= texelFetch(iChannel1, ivec2(KEY_Q, 0), 0).r;

        float zAxis = texelFetch(iChannel1, ivec2(KEY_W, 0), 0).r;
        zAxis -= texelFetch(iChannel1, ivec2(KEY_S, 0), 0).r;

        float hAxis = texelFetch(iChannel1, ivec2(KEY_RIGHT, 0), 0).r;
        hAxis -= texelFetch(iChannel1, ivec2(KEY_LEFT, 0), 0).r;

        float vAxis = texelFetch(iChannel1, ivec2(KEY_UP, 0), 0).r;
        vAxis -= texelFetch(iChannel1, ivec2(KEY_DOWN, 0), 0).r;

        pos.x += (cos(rot.x) * xAxis + sin(rot.x) * zAxis) * iTimeDelta;
        pos.y += yAxis * iTimeDelta;
        pos.z += (cos(rot.x) * zAxis + -sin(rot.x) * xAxis) * iTimeDelta;

        rot.x += iTimeDelta * hAxis;
        rot.y += iTimeDelta * vAxis;

        rot.y = min(max(rot.y, -1.5707), 1.5707);
        if (ipos.x == 0)
        {
            fragColor = vec4(pos, 0.0);
        }
        else
        {
            fragColor = vec4(rot, 0.0);
        }
    }

    if (iFrame == 0)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    }
}

