        u.x < .5 ? u+=t, O-- : O=-O,
        u.y < .5 ? u-=t, O++ : O--;
}

vec4 ViewAsSlice(vec3 uv3, vec3 bufferSize)
{
    float x = Convert3DTo1D(uv3,  bufferSize);
    vec2 uv3D = Convert1DTo2D(x, iResolution.xy) / iResolution.xy;
    return vec4(texture(iChannel0, uv3D));
}

vec4 ViewAsRayMarching(vec2 uv, vec3 bufferSize, float density, int fbmCount, float dir)
{
    //view ray marching
    int stepCount = 64;
    float stepSize = 0.05;

    vec3 ro = vec3(0, 0, dir * iTime * 1.5);   
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));;

    vec4 colorSum = vec4(0);
    for(int i = 0; i < stepCount; i++)
    {
        vec3 pos = ro + rd * float(i) * stepSize;

        float noiseSize = 32.;

        float freq = 1.;
        float amp = 1.;

        for(int j = 0; j < fbmCount; j++)
        {
            float x = Convert3DTo1D(pos * noiseSize * freq,  bufferSize);
            vec2 channelResolution = iChannelResolution[0].xy;
            vec2 uv3D = Convert1DTo2D(x, channelResolution.xy) / channelResolution.xy; //must match buffer A resolution

            vec4 sampleColor = vec4(texture(iChannel0, uv3D));
            colorSum += sampleColor * stepSize * density * amp;

            freq *= 2.0;
            amp *= 0.5;
        }
    }

    return colorSum;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
    vec3 bufferSize = vec3(64.); //must match Buffer A size's value

    if(fragCoord.x < iResolution.x / 2.0)
    {   
        //view a slice
        vec3 uv3 = vec3(fragCoord / 4., iTime * 16.);
        fragColor = ViewAsSlice(uv3, bufferSize);
    }
    else 
    {       
        vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
        bool upperHalf = (fragCoord.y > iResolution.y / 2.0);
        int fbmCount = upperHalf ? 1 : 3;
        float density = upperHalf ? 0.5 : 0.25;
        float dir = upperHalf ? -1. : 1.;
        fragColor = ViewAsRayMarching(uv, bufferSize, density, fbmCount, dir);     
    }
}

