#define M iMouse.xy
#define W iResolution.x
#define H iResolution.y
#define line vec3
#define point vec2
#define EPSILON 0.0001
#define INFINITY 1e7
#define AA( uv ) ( 0.5 * ( dFdx( uv.x ) + dFdy( uv.y ) ) )

#define CYAN vec4( 0, 1, 1, 1 )
#define MAGENTA vec4( 1, 0, 1, 1 )
#define YELLOW vec4( 1, 1, 0, 1 )

// line that joins two points
line join( point P1, point P2 )
{
	return vec3( P1.y - P2.y, P2.x - P1.x, P1.x * P2.y - P1.y * P2.x );
}

// point that meets two lines
point meet( line L1, line L2 ) {
    return point( L1.y * L2.z - L1.z * L2.y, L1.z * L2.x - L1.x * L2.z ) / ( L1.x * L2.y - L1.y * L2.x );
}

// "distance" between line and point
float dist( line L, point P )
{
	return ( P.x * L.x + P.y * L.y );
}

// "distance" between point and line
float dist( point P, line L )
{
	return ( dist( L, P ) + L.z ) / ( L.x * L.x + L.y * L.y );
}

// project point to line
point project( point P, line L )
{
	return P - ( dist( P, L ) * L.xy );
}

// project line to point
line project( line L, point P )
{
	return line( L.xy, -( P.x * L.x + P.y * L.y ) );
}

// reflect point across line (2x projection)
point mirror( point P, line L )
{
	return P - ( 2. * dist( P, L ) * L.xy );
}

// reflect line across point (2x projection)
line mirror(line L, point P) {
    return line(L.xy, L.z - dot(L.xy, 2.0 * (P - project(P, L))));
}


//

vec4 draw_col = vec4( 0 );

void draw_line( in vec2 uv, in line L, in vec4 line_col )
{
	float dist = abs( dist( L, uv ) + L.z ) / length( L.xy );
	float aa = AA( uv );
	draw_col = mix( draw_col, line_col, smoothstep( aa, -aa, dist - 2. ) );
}

void draw_point( in vec2 uv, point P, in vec4 point_col )
{
	float dist = length( uv - P );
	float aa = AA( uv );
	draw_col = mix( draw_col, point_col, smoothstep( aa, -aa, dist - 12. ) );
}

void mainImage( out vec4 col, in vec2 uv )
{
	point P11 = point( 32, 32 );
	point P12 = M;
	point P21 = point( 64, ( H / 2. ) - ( sin( iTime * 2. ) * 64. ) );
	point P22 = point( ( W / 2. ) - ( cos( iTime * 2. ) * 64. ), 64 );

	line L1 = join( P11, P12 );
	line L2 = join( P21, P22 );

	point PM1 = meet( L1, L2 );
	point PP0 = point( ( W / 2. ) + ( sin( iTime ) * 64. ), ( H - 128. ) + ( cos( iTime ) * 64. ) );
	point PP1 = project( PP0, L1 );
	line LP1 = mirror( L1, PP0 );
	line LP2 = join( PP1, P22 );

	draw_line( uv, L1, CYAN );
	draw_line( uv, L2, MAGENTA );
	draw_line( uv, LP1, CYAN );
	draw_line( uv, join( PP0, PP1 ), YELLOW );
	draw_line( uv, LP2, MAGENTA );

	draw_point( uv, P11, MAGENTA );
	draw_point( uv, P12, CYAN );
	draw_point( uv, mirror( P12, LP2 ), CYAN );
	draw_point( uv, P21, MAGENTA );
	draw_point( uv, P22, MAGENTA );

	draw_point( uv, PM1, YELLOW );
	draw_point( uv, PP0, YELLOW );
	draw_point( uv, PP1, YELLOW );

	col = draw_col;
}

// 'Star Wars: The Force Awakens' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/DtjfRh
// YouTube: https://youtu.be/WMf8LMZy3l0
//
// Processed by 'GLSL Shader Shrinker'
// (https://github.com/deanthecoder/GLSLShaderShrinker)
//
// Tricks to aid performance:
//   - Precalculate function results and simplify calculations
//     when possible (see GLSL Shader Shrinker).
//   - SDF fine details only modelled when ray is near a surface.
//   - Using bump maps over SDF displacement where possible.
//     (Displacing an SDF is much more expensive at the code is
//     ever time the ray moves, whereas a bump is only applied once
//     when the ray has hit a surface.)
//   - AA only applied on pixels where the neighbouring
//     pixel is a significantly different color.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane,
// totetmatt, Blackle, Dave Hoskins, byt3_m3chanic, tater,
// and a bunch of others for sharing their time and knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
#define CHROMATIC	0.003
#define LENS	0.005

#define _res iResolution
#define _tex0 iChannel0

vec2 fisheye(vec2 uv) {
	uv -= 0.5;
	uv *= pow(dot(uv, uv), LENS);
	return uv + 0.5;
}

// Thnx Dave_Hoskins
vec2 h22(vec2 p) {
	vec3 v = fract(p.xyx * vec3(.1031, .1030, .0973));
	v += dot(v, v.yzx + 333.33);
	return fract((v.xx + v.yz) * v.zy);
}

float h21(vec2 p) {
	p += 353.627;
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .11369, .13787));
	p3 += dot(p3, p3.yzx + 19.19);
	return fract((p3.x + p3.y) * p3.z);
}

float film(vec2 uv) {
	// Film grain.
	float c = (h21(uv * _res.xy) - 0.5) / 64.;

	// Lens dust.
	uv *= 4.;
	float sm = 0.1;
	for (int i = 0; i < 3; i++, uv++) {
		vec2 p = fract(uv) - 0.5,
		     id = floor(uv);
		float s = h21(id) * sm;
		p += h22(id) * (1.0 - sm) * 0.5;
		c = max(c, smoothstep(s, s * 0.5, length(p)) * 0.02);
		sm *= 0.5;
		uv *= 2.282;
	}

	return c;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
	vec2 uv = fragCoord / _res.xy;

	// Lens distortion.
	uv = fisheye(uv);

	// Chromatic aberration.
	vec2 ca = (uv - 0.5) * CHROMATIC;
	fragColor = vec4(texture(_tex0, uv + ca).r, texture(_tex0, uv).g, texture(_tex0, uv - ca).ba);
	if (fragColor.w > 0.0) fragColor.rgb += film(uv * vec2(1, _res.y / _res.x));

	// Color correction.
	fragColor = pow(fragColor, vec4(0.4545));

	// Vignette.
	uv -= 0.5;
	fragColor *= 1.0 - 0.5 * dot(uv, uv);
}

