//#define AA    // Disable if your GPU is melting.

#define LIGHT_RGB	vec3(2, 1.6, 1.4)
#define R	_res
#define Z0	min(_t, 0.)
#define I0	min(int(_t), 0)
#define sat(x)	clamp(x, 0., 1.)
#define S	smoothstep
#define S01(a)	S(0., 1., a)

#define _res iResolution
#define _tex0 iChannel0
#define _t iTime

float t, xw, roll,
      fade = 1.;
vec2 g;
vec3 lp, xp;

struct Hit {
	float d, id;
	vec3 p;
};

void U(inout Hit h, float d, float id, vec3 p) { if (d < h.d) h = Hit(d, id, p); }

float max2(vec2 v) { return max(v.x, v.y); }

float max3(vec3 v) { return max(v.x, max(v.y, v.z)); }

float dot3(vec3 v) { return dot(v, v); }

float sum2(vec2 v) { return dot(v, vec2(1)); }

float mul2(vec2 v) { return v.x * v.y; }

vec2 h22(vec2 p) {
	vec3 v = fract(p.xyx * vec3(.1031, .103, .0973));
	v += dot(v, v.yzx + 333.33);
	return fract((v.xx + v.yz) * v.zy);
}

float h31(vec3 p) {
	p = fract(p * .3183099 + .1);
	p *= 17.;
	return fract(p.x * p.y * p.z * (p.x + p.y + p.z));
}

float h21(vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(.1031, .11369, .13787));
	p3 += dot(p3, p3.yzx + 19.19);
	return fract((p3.x + p3.y) * p3.z);
}

float n21(vec2 p) {
	vec2 i = floor(p),
	     f = fract(p);
	f = f * f * (3. - 2. * f);
	return mix(mix(h21(i), h21(i + vec2(1, 0)), f.x), mix(h21(i + vec2(0, 1)), h21(i + 1.), f.x), f.y);
}

float n31(vec3 p) {
	vec3 i = floor(p),
	     f = fract(p);
	f = f * f * (3. - 2. * f);
	return mix(mix(mix(h31(i), h31(i + vec3(1, 0, 0)), f.x), mix(h31(i + vec3(0, 1, 0)), h31(i + vec3(1, 1, 0)), f.x), f.y), mix(mix(h31(i + vec3(0, 0, 1)), h31(i + vec3(1, 0, 1)), f.x), mix(h31(i + vec3(0, 1, 1)), h31(i + 1.), f.x), f.y), f.z);
}

float fbm(vec3 p, float roughness) {
	int octaves = 4;
	float sum = 0.,
	      amp = 1.,
	      tot = 0.;
	roughness = sat(roughness);
	for (int i = I0; i < octaves; i++) {
		sum += amp * n31(p);
		tot += amp;
		amp *= roughness;
		p *= 2.;
	}
	return sum / tot;
}

vec3 randomPos(float seed) {
	vec4 s = vec4(seed, 0, 1, 2);
	return vec3(h21(s.xy), h21(s.xz), h21(s.xw)) * 1e2 + 1e2;
}

float fbmDistorted(vec3 p, float roughness, float distortion) {
	p += (vec3(n31(p + randomPos(0.)), n31(p + randomPos(1.)), n31(p + randomPos(2.))) * 2. - 1.) * distortion;
	return fbm(p, roughness);
}

float sabs(float f) { return sqrt(f * f + .1); }

float smin(float a, float b, float k) {
	float h = sat(.5 + .5 * (b - a) / k);
	return mix(b, a, h) - k * h * (1. - h);
}

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

vec3 ryz(vec3 p, float a) {
	p.yz *= rot(a);
	return p;
}

vec3 dx(vec3 p, float e) {
	p.x += e;
	return p;
}

vec3 dy(vec3 p, float e) {
	p.y += e;
	return p;
}

vec3 dz(vec3 p, float e) {
	p.z += e;
	return p;
}

vec3 ay(vec3 p, float d) { return vec3(p.x, abs(p.y) - d, p.z); }

vec3 az(vec3 p) { return vec3(p.xy, abs(p.z) + .05); }

float weld(float a, float b, float r) {
	// Thanks Mercury - http://mercury.sexy/hg_sdf/
	return min(min(a, b), (a - r + b) * .70711);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max3(q), 0.);
}

float cyl(vec3 p, vec2 hr) {
	vec2 d = abs(vec2(length(p.zy), p.x)) - hr;
	return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float cap(vec3 p, float r) {
	p.x -= clamp(p.x, 0., .3);
	return length(p) - r;
}

float hex3D(vec3 p, vec2 h) {
	const vec3 k = vec3(-.8660254, .5, .57735);
	p = abs(p);
	p.zy -= 2. * min(dot(k.xy, p.zy), 0.) * k.xy;
	vec2 d = vec2(length(p.zy - vec2(clamp(p.z, -k.z * h.x, k.z * h.x), h.x)) * sign(p.y - h.x), p.x - h.y);
	return min(max2(d), 0.) + length(max(d, 0.));
}

float greeble(vec3 p) {
	float h = .005,
	      d = p.y;
	for (float i = 1.; i <= 3.; i++) {
		vec3 q = p;
		q.xz = fract(p.xz) - .5;
		vec2 r = h22(floor(p.xz) + i * 2.2);
		d = min(d, box(q, vec3(.05 + .35 * r, h * (.5 + .5 * fract(r.x * 157.324))).xzy));
		h *= 1.5;
		p.xz *= 1.5;
		p.xz += .5 / i;
	}

	return d * .8;
}

vec3 rayDir(vec3 ro, vec3 lookAt, vec2 uv) {
	vec3 f = normalize(lookAt - ro),
	     r = normalize(cross(vec3(0, 1, 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}

vec3 skyCol(float y) { return pow(vec3(max(1. - y * .5, 0.)), vec3(6, 3, 1.5)) * vec3(.95, 1, 1) * (1. - pow(vec3(2), -vec3(35, 14, 7))); }

vec3 sky(vec3 rd) {
	vec3 p,
	     col = skyCol(rd.y);
	float den,
	      d = 10. / rd.y;
	if (d < 0.) return col;
	p = rd * d + vec3(4, .2, 1) * t * .2;
	p.xz *= .2;
	den = 1.;
	for (int i = 0; i < 3; i++)
		den *= exp(-.06 * fbm(p, .5));

	return mix(col, LIGHT_RGB, S(.8, 1., den) * (1. - sat(d / 1e2)));
}

float fakeEnv(vec3 n) {
	// Thanks Blackle.
	return length(sin(n * 2.5) * .5 + .5) / 1.73205;
}

float texScratches(vec3 p) {
	float sh = S(.85, 1., n31(p * vec3(80, 12, 1)));
	p.xy = p.yx * mat2(.76031, .64956, -.64956, .76031);
	return max(sh, S(.85, 1., n31(p * vec3(80, 10, 1))));
}

float taperHex(vec3 p, float w, float h1, float h2, float l, float s, float wt) {
	w *= 1. - (p.x / l + .5) * wt;
	return max(hex3D(p, vec2(w * .5, l * .5) - s) - s, abs(p.y) - mix(h1, h2, p.x / l + .5) * .5);
}

float wing(inout vec3 op) {
	op.x += .25;
	op.z = abs(op.z);
	op = ryz(ay(dz(op, -.26), .02), xw * -.2);
	vec3 q,
	     p = op;

	// Wing.
	float d, l,
	      f = .05;
	f += p.z * .1;
	f += .065 * step(p.z, .5);
	d = box(dx(p, -f), vec3(.4 - f, .005, 1.6));
	p.x -= .15;
	p.z -= .4;

	// Engine.
	f = .2 - .05 * S(.2, 0., p.x);
	d = weld(d, max(cyl(p, vec2(f, .45)), -p.y - .005), .04 * step(0., p.y));

	// Exhaust.
	q = p - vec3(-.72, .16, 0);
	f = .04 - .003 * sat(sin(atan(q.y, q.z) * 16.)) * S(.05, .04, q.x);
	f += .003 * S(.02, .03, abs(abs(q.x - .15) - .05));
	f = cap(q, f);
	l = length(q + vec3(.06, 0, 0));
	f = max(f, .08 - l);
	d = weld(d, f, .015);
	g.x += .02 / (.01 + l * l * 10.) * S(.05, -.05, q.x);

	// Inlet.
	d = smin(d, -max(max(length(p.yz) - .17, -.02 - p.y), abs(p.x - .45) - .05), -.05);

	// Gunz.
	p -= vec3(.05, .026, 1.2);
	d = min(d, cyl(p, vec2(.03 - .01 * S(.31, .32, abs(p.x)), .33)));
	p.x -= .5;
	d = min(d, cyl(p, vec2(step(p.x, 0.) * .005, .4)));
	p.x -= .32;
	p.yz *= mat2(.49757, .86742, -.86742, .49757);
	f = length(p.xy);
	return min(d, max(max(max(f - .04, .045 - f), p.x), abs(p.z) - .006)) * .95;
}

float mountains(vec3 p) {
	p.z = abs(p.z);
	p += vec3(32. * t * .3, 2, -45);
	float d = length(p.yz);

	if (d < 13.0)
	    d -= fbm(vec3(p.x * .5, 0, p.z) * .1, .4) * 12.;
	return smin(d, p.y + 2., 8.) * .9;
}

Hit sdf(vec3 p) {
	p.x += .5;

	vec3 q,
	     op = p;
	Hit h;
	h.d = 1e7;
	U(h, mountains(p), 7., p);

	// Movement.
	p -= xp * vec3(1, 2, 1);
	p.yz *= rot(roll);

	// Rear body.
	float f, d = taperHex(az(p), .6, .7, .5, .9, 0., .19);
	d = smin(d, max(box(p, vec3(.45, .35, .2)), -p.y), .02);
	d = max(d, abs(p.z) + p.y * .45 - .3);
	d = max(d, p.y - p.x * .2 - .38);
	d = max(d, -p.y - p.x * .1 - .24);

	// Rear end bit.
	d = weld(d, box(ay(dy(dx(p, .4), -.05), .1), vec3(.12, .08, .08)), .02);

	// Wings.
	q = p;
	if (abs(p.z) < 7.0)
	    U(h, wing(q), 3.005, q);

	// Fuselage.
	q = p;
	q.x -= 1.7;
	d = min(d, taperHex(q, .4, .36, .21, 2.5, 0., .3));

	// Nose.
	q.x -= 1.45;
	f = taperHex(q, .32, .26, .05, .6, .03, .4);
	f = max(f, q.y * .5 - q.x - .25);
	q.y -= .1;
	f = max(f, -box(q, vec3(.15, .04, .06)));
	d = min(d, f);

	// BB-8
	q = p - vec3(.15, .37, 0);
	f = box(q, vec3(.23, .05, .1));
	d = max(d, -f);

	if (f < 0.1)
	    f = max(f, greeble(dy(p, -.32) * vec3(6, 1, 6)));
	d = min(d, f);
	f = length(p - vec3(.25, .31, 0));
	d = max(d, .082 - f);
	U(h, d, 2.005, p);
	q = p - vec3(.25, .37, 0);
	U(h, f - .05, 8.01, q);
	q.xz *= rot(n21(vec2(t, t)) * 2.5 - 1.8);
	U(h, length(q) - .03, 8.01, q);
	d = max(cyl(dy(q, -.015), vec2(0, .032)), -q.x);
	d = min(d, cyl(dy(q.yxz, .01), vec2(-.008, .06)));
	U(h, d, 6.045, q);

	// Cockpit.
	q = p;
	q.xy -= vec2(.865, .16);
	d = taperHex(q, .35, .4, .02, .8, 0., .4);
	U(h, d, 6.045, p);

	// Water level.
	p = op;
	d = p.y + 2.;

	// Water plume.
	if (xp.y < 0.8) {
    	q = p.zyx;
    	q.z += 2. + xp.y * 10.;
    	q.x = sabs(q.x) - 2. * (1. - xp.y) + q.z * .3;
    	q.xy *= rot(-.4 * (1. - xp.y));
    	q.y += 5. + S(-4., 0., p.x) + xp.y * 4.;
    	f = cyl(q, vec2(4, sin((p.x + sign(p.z) * 4.) * .2 + t * 5.5) * .2));
    	d = smin(d, f, 1. + 2. * S(0., -6., p.x) + sat(abs(p.z) * .5));
    	g.y += .01 / (.1 + f * f) * S(-.2, .1, q.x);
	}

	U(h, d, 4.3, p);

	// Nothing is perfectly sharp.
	h.d -= .01;
	return h;
}

vec3 N(vec3 p, float d) {
	float h = d * .05;
	vec3 n = vec3(0);
	for (int i = I0; i < 4; i++) {
		vec3 e = .005773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * sdf(p + e * h).d;
	}

	return normalize(n);
}

float shadow(vec3 p, vec3 ld, vec3 n) {
	// Quick abort if light is behind the normal.
	if (dot(ld, n) < -.1) return 0.;

	float s = 1.,
	      l = .01,
	      mxL = length(p - lp);
	for (float i = Z0; i < 30.; i++) {
		float d = sdf(l * ld + p).d;
		s = min(s, mix(50., 7., .8) * d / l);
		l += max(.03, d);
		if (mxL - l < .5 || s < .001) break;
	}

	return S01(s);
}

float ao(vec3 p, vec3 n) {
	const vec2 h = vec2(.1, 1);
	vec2 ao;
	for (int i = I0; i < 2; i++)
		ao[i] = sdf(h[i] * n + p).d;

	return sat(mul2(.2 + .8 * ao / h));
}

vec3 lights(vec3 p, vec3 ro, vec3 rd, inout vec3 n, Hit h, float fogAdjust) {
	if (h.id == 0.) return sky(rd);
	float _ao, sha, fogY, fg,
	      spe = 10.,
	      shine = 1.;
	vec3 c, l, col, uv,
	     ld = normalize(lp - p);

	// Calculate ambient occlusion and shadows.
	_ao = ao(p, n);
	sha = shadow(p, ld, n);

	if (h.id == 6.045) {
		c = vec3(-.01);
		shine = 1.5 + n31(h.p * 40.);
	}
	else if (h.id == 2.005 || h.id == 3.005) {
		shine = 5.;
		spe = 150.;
		c = vec3(.03);

		// Orange.
		float f,
		      b = 0.;
		if (h.id == 2.005) {
			// Fuselage.
			if (abs(h.p.y - .035) < .04 && h.p.x > -.42 && h.p.x < 2.875 + h.p.y * .6) b = S(1., .995, sin(h.p.x * 5.));
			else b = float(abs(h.p.x - 2.2) < .2 && h.p.y > .09);
		}
		else {
			// Wings.
			if (abs(h.p.x - .52) < .02 && h.p.z < 1.) b = 1.;
			else b = float(abs(h.p.z - 1.1) < .35 && h.p.x - abs(h.p.z) * .15 > 0.);

			if (abs(h.p.x - .6) < .02 && h.p.z < 1.) c = vec3(.1, .1, .15);
		}

		b *= .5 + .5 * n31(h.p * 40.);
		c = mix(c, vec3(.4, .12, .01), b);

		// BB-8's nest.
		if (abs(h.p.x - .145) < .21 && abs(h.p.z) < .088 && h.p.y > .334) c *= 8.;

		// Dirt/scratches.
		f = texScratches(h.p.zyx * vec3(2, 12, 15) + 13.34);
		c += f * .1;
		shine *= 1. + f * 5.;
		c *= .1 + .9 * n31(h.p * 8.);
	}
	else if (h.id == 4.3) {
		c = vec3(.07, .12, .1);

		// Bump map the surface detail.
		float f = n31((p + vec3(t * 32., t, 0)) * vec3(1, 1, 10)) - .5;
		n = normalize(n + f * .1);

		// Plume.
		f = S(0., 1., h.p.y + 2.);
		f += S(.8, 1., h.p.y + 2.);
		c += vec3(.2 + 1. * fbmDistorted(p + vec3(t * 9.6, 0, 0), f, 3. * f)) * f;
		n = normalize(dz(n, sin(p.z * 8. * (1. - xp.y)) * f * .5));
	}
	else if (h.id == 7.) c = vec3(.1, .12, .06);
	else{
		c = vec3(.35, .35, .4);
		if (abs(h.p.y + .015) < .005 || abs(abs(h.p.y - .015) - .015) < .002 || length(h.p.yz + vec2(.06, 0)) < .03) c = vec3(.32, .1, .08);
		else if (abs(h.p.y - .036) < .002) c *= .5;
	}

	l = sat(vec3(dot(ld, n),  // Key light.
	dot(-ld.xz, n.xz),  // Reverse light.
	n.y // Sky light.
	));

	l.x *= fakeEnv(ld * 4.); // Light mask.
	l.xy = .1 + .9 * l.xy; // Diffuse.
	l.yz *= _ao; // Ambient occlusion.
	l *= vec3(1, .6, .2); // Light contributions (key, reverse, sky).

	// Apply tinted shadows.
	l.x *= .1 + .9 * sha;
	vec3 skyTop = pow(vec3(max(.5, 0.)), vec3(6, 3, 1.5)) * vec3(.95, 1, 1) * (1. - pow(vec3(2), -vec3(35, 14, 7)));
	c += skyTop * (1. - sha) * .2;

	// Specular (Blinn-Phong)
	shine *= sha; // No specular in the shadows.
	l.x += pow(sat(dot(normalize(ld - rd), n)), spe) * shine;

	// Light falloff.
	l.x *= dot(lp, lp) / (1. + dot(lp - p, lp - p));

	// Combine the lights (key, reverse, sky).
	col = mix((sum2(l.xy) * LIGHT_RGB + l.z * skyTop) * c, skyTop, S(.6, 1., 1. + dot(rd, n)) * .02);

	// Simple fog layer.
	fogY = -1.; // Fog surface Y.
	uv = vec3(p.xz, fogY) * .4 + t * vec3(32, 0, .2); // Fog uv.
	float fogTex = fbm(uv, .5); // Depth (distance) from fogY to max fog.
	fogY -= (1. - fogTex) * .3; // Surface deviation.
	fg = S(0., -.3, p.y - fogY);

	fg *= .1 + .3 * fogTex; // Fog texture.
	fg *= 1. - sat(-rd.y); // Shallow angle = More fog.

	// Distance Fog.
	fg += (1. - exp(dot3(p - ro) / -fogAdjust * 1e-4));

	return mix(pow(vec3(max(1., 0.)), vec3(6, 3, 1.5)) * vec3(.95, 1, 1) * (1. - pow(vec3(2), -vec3(35, 14, 7))), col, 1. - sat(fg));
}

vec3 xwingPos() {
	vec3 p = vec3(0, .8 + .2 * sin(t * .5), sin(t) + sin(t * .3));
	float f = S(14., 10., t) + S(18., 20., t) * 1.1;
	p.zy *= f;
	roll = (cos(t) + .3 * cos(t * .3)) * .1;
	xw = S(12., 14., t);
	p.xz *= .1 + .9 * S(3., 6., t);
	return p;
}

float addFade() { return min(1., abs(t)); }

vec4 march(vec3 ro, vec3 rd) {
	g = vec2(0);
	fade = addFade();
	xp = xwingPos();
	lp = vec3(0, .001, -20);
	lp.yz *= mat2(.41305, -.91071, .91071, .41305);
	lp.xz *= mat2(.4536, -.89121, .89121, .4536);

	// March the scene.
	vec3 n,
	     p = ro,
	     col = vec3(0);
	float i, dof,
	      d = 1.;
	Hit h;
	for (i = Z0; i < 1e2; i++) {
		if (d > 1e2) {
			h.id = 0.;
			break;
		}

		h = sdf(p);
		if (abs(h.d) < 2e-4 * d) break;
		d += h.d;
		p += h.d * rd;
	}

	dof = sat(d / 1e2);

	// Brighten scene when facing the sun.
	col += .2 * dof * LIGHT_RGB * S(.5, 1., pow(sat(dot(rd, normalize(lp))), 3.));

	// Engine glow.
	col += g.x * vec3(1, .2, .25) * (.7 + .5 * n31(dx(p, 10.8 * t)));
	col += g.y * LIGHT_RGB;

	n = N(p, d);

	col += lights(p, ro, rd, n, h, 1.);
	if (fract(h.id) > 0.) {
		// We hit a reflective surface, so march reflections.
		float opac = 1.,
		      refl = fract(h.id);
		rd = reflect(rd, n);
		p += n * .01;
		ro = p;
		d = .01;
		for (i = Z0; i < 64.; i++) {
			if (d > 20.) {
				h.id = 0.;
				break;
			}

			h = sdf(p);
			if (abs(h.d) < 2e-4 * d) break;
			d += h.d;
			p += h.d * rd;
		}

		// Add a hint of the reflected color.
		opac *= refl;
		n = N(p, d);
		col += opac * (1. - col) * lights(p, ro, rd, n, h, .2);
	}

	return max(vec4(0), vec4(col, dof));
}

void mainImage(out vec4 fragColor, vec2 fc) {
	t = mod(_t, 60.);
	vec2 uv = (fc - .5 * R.xy) / R.y;
	vec3 col,
	     lookAt = vec3(0, 1.6, 0),
	     ro = vec3(0, 1.001, 0);
	ro.x += 4. * (S(10., 14., t) - S(20., 24., t) * 1.5);
	lookAt.y -= S(10., 14., t) - S(32., 35., t);
	ro.z = mix(.12, .3, S(0., 7., t)) * -20.;
	ro.yz *= rot(mix(.4, .14, S(4., 6., t)) * -1.57);
	ro.xz *= rot((mix(.25, .54, S(5., 10., t)) * 2. - 1.) * -3.1415);
	ro += vec3(1, .5, 2) * S(25., 30., t);

	// View bob.
	ro += .5 * sin(t * vec3(.9, .7, .3));
	vec4 rgbz = march(ro, rayDir(ro, lookAt, uv));
	col = rgbz.rgb;

#ifdef AA
	if (fwidth(col.r) > .1) {
