// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define BLUR 6.0

#define _res iResolution
#define _tex0 iChannel0

float h31(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 333.3456);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord / _res.xy;

    // Apply basic depth of field blur.
    vec4 col = texture(_tex0, uv);
    float blur = smoothstep(0.5, 1.0, col.w) * BLUR;

    if (blur > 0.0) {
        float samples = 40.0;
        for (float i = 0.0; i < samples; i++) {
            float a = 3.1415 * 2.0 / samples * i;
            vec2 tuv = uv + vec2(blur * h31(vec3(uv, i)), 0) * mat2(cos(a), sin(a), -sin(a), cos(a)) / _res.xy;
            col += texture(_tex0, tuv);
        }

        col /= samples + 1.0;
    }

    fragColor = col;    
}

