float SpicyGradientTime(vec2 uv, float t)
{
    //Use the uv to offset value to sample the gradient
    t += uv.y * 0.1 + sin(uv.x * 25. + t * 5.) * 0.005 + sin(uv.y * 150. + t * 5.) * 0.005;
    t = fract(t);
    return t;
}

vec2 DiamondUV(vec2 uv)
{
    uv.x *= 1.5; //form stretched diamond

    vec2 diamondUV = Rotate(uv, radians(45.));
    diamondUV = Quantize(diamondUV, vec2(25.));
    diamondUV = Rotate(diamondUV, -radians(45.));

    return diamondUV;
}

vec4 SampleGradient(float t)
{
    t = fract(t);

    const int size = 8;  
    vec4 cols[size] = vec4[size]
    (
        GetCol(86, 167, 204),
        GetCol(179, 225, 232),
        GetCol(66, 214, 255),
        GetCol(235, 214, 163),
        GetCol(255, 163, 163),
        GetCol(95, 237, 194), 
        GetCol(29, 26, 176),
        GetCol(96, 63, 204)
    );

    float sizeT = t * float(size);
    int id1 = int(sizeT);
    int id2 = (id1 + 1);

    float tt = unlerp(sizeT, float(id1), float(id2));
    tt = easeInOutCubic(tt);
    return mix(cols[id1], cols[id2 % size], tt);    
}

