vec4 GetCol(int r, int g, int b)
{
    return vec4(r, g, b, 255.) / 255.;
}

float unlerp(float x, float fromMin, float fromMax)
{
    return (x - fromMin) / (fromMax - fromMin);
}

float saturate(float x)
{
    return clamp(x, 0., 1.);
}

vec2 Quantize(vec2 x, vec2 resolution)
{
    return floor(x * resolution) / resolution;
}

vec2 Rotate(vec2 p, float a)
{
    float c = cos(a);
    float s = sin(a);
    return vec2(
        p.x * c - p.y * s,   
        p.x * s + p.y * c);
}

float easeInOutCubic(float x)
{
    return x < 0.5 ? 4. * x * x * x : 1. - pow(-2. * x + 2., 3.) / 2.;
}

