void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int channelWidth = 1; // width of a single color channel in pixels
    int channelHeight = 3; // height of color channels in pixels
    int hGap = 1; // horizontal distance between two neighboring pixels
    int vGap = 1; // vertical distance between two neighboring pixels

    float columns = float(channelWidth * 3 + hGap);
    float pixelHeight = float(channelHeight + vGap);

    vec2 sampleRes = vec2(iResolution.x / columns, iResolution.y / pixelHeight);
    vec2 pixel = vec2(floor(fragCoord.x / columns), floor(fragCoord.y / pixelHeight));
    vec2 sampleUv = pixel / sampleRes;

    // color of sample point
    vec3 col = texture(iChannel0, sampleUv).xyz;

    int column = int(fragCoord.x) % (channelWidth * 3 + hGap);

    // set color based on which channel this fragment corresponds to
    if (column < channelWidth * 1) col = vec3(col.x, 0.0, 0.0);
    else if (column < channelWidth * 2) col = vec3(0.0, col.y, 0.0);
    else if (column < channelWidth * 3) col = vec3(0.0, 0.0, col.z);
    else col = vec3(0.0, 0.0, 0.0);

    // offset every other column of pixels
    int height = int(pixelHeight);
    if (int(pixel.x) % 2 == 0) {
        if (int(fragCoord.y) % height >= height - vGap) col = vec3(0.0, 0.0, 0.0);
    } else {
        if (int(fragCoord.y) % height < vGap) col = vec3(0.0, 0.0, 0.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

