#define freq(f) texture(iChannel0, vec2(f, 0.25)).x * 0.8
float avgFreq(float start, float end, float step) {
    float div = 0.0;
    float total = 0.0;
    for (float pos = start; pos < end; pos += step) {
        div += 1.0;
        total += freq(pos);
    }
    return total / div;
}


vec3 palette( float t ) {

    float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.2);
    float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
    float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.85);

    vec3 a = vec3(0.5, 0.5, 0.5)/sin(bassFreq);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557)/sin(bassFreq/2.);

    return a + b*cos( 6.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 coll = palette(length(uv));

    vec3 col = vec3(0);

    float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.2);
    float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
    float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.85);

    float a = .7853982;
    float s = sin(a);
    float c = cos(a);
    uv *= mat2(c, -s, s, c);
    uv *= 20.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor( uv);

    float m = 0.-bassFreq;
    float t = iTime+sin(medFreq);



    for(float y=-1. ; y<=1. ; y++) {
        for(float x=-1. ; x<=1. ; x++) {
            vec2 offs = vec2(x, y);

            float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.85);
            float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
            float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.85);


            float d = length(gv-offs)+mix(.1, .5, sin(medFreq));
            float dist = length(id+offs)*.4;

            float r = mix(.3, 1.5, sin(dist-t)*.5+.5)+mix(.1, .8, sin(bassFreq));
            m += smoothstep(r,r*.9, d)-sin(bassFreq/6.);

            }
        }
    float y=-1. ; y<=1. ; y++;    
    float x=-1. ; x<=1. ; x++;    
    vec2 offs = vec2(x, y); 

    float dist = length(id+offs)*.4;
    //col.rg = gv;
    col += mod(m, 1.2);
    col *= coll + sin(dist-t)*medFreq;

    fragColor = vec4(col,1.0);
}

// CC0: A simple raymarched egg
//  So I was messing around build a raymarched for a terminal window
//  (Silly idea) so I needed a simple raytracer that look "ok" in 
//  80x60 resolution
//  Build this simple raymarched egg
//  Thought it could be nice to share as I believe shadertoy benefit
//  both from overworked glass shaders like my last but also simpler ones
//  like this

#define TIME        iTime
#define RESOLUTION  iResolution
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

const float PI                  = acos(-1.)   ;
const float TAU                 = 2.0*PI      ;
const float TOLERANCE           = 1.0E-2      ;
const float MAX_RAY_LENGTH      = 20.         ;
const float NORM_OFF            = 0.001       ;
const float MAX_RAY_MARCHES     = 50.0        ;
const float MAX_SHADOW_MARCHES  = 25.0        ;

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const vec3 SUNDIR           = normalize(vec3(1.0, 1.2, 1.0))   ;
const vec3 SKYDIR           = normalize(vec3(-1.0, 3.0, -1.0))   ;
const vec3 SKYCOL           = HSV2RGB(vec3(0.55, 0.8, 0.8));   
const vec3 GROUNDCOL        = HSV2RGB(vec3(0.15, 0.8, 0.8));   
const vec3 EGGCOL           = HSV2RGB(vec3(50./360., 0.24, 1.0));

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions2d/
float egg(vec2 p, vec2 dim) {
  const float k = sqrt(3.0);
  p.x = abs(p.x);
  float r = dim.x - dim.y;
  return ((p.y<0.0)       ? length(vec2(p.x,  p.y    )) - r :
          (k*(p.x+r)<p.y) ? length(vec2(p.x,  p.y-k*r)) :
                            length(vec2(p.x+r,p.y    )) - 2.0*r) - dim.y;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions/
float egg(vec3 p, vec3 dim) {
  // More specifically this is the opRevolution from IQ's article that allows
  //  to use a 2D distance field and rotate it around Y axis to produce a 3D distance field
  //  Very useful.
  vec2 q = vec2(length(p.xz) - dim.z, p.y);
  return egg(q, dim.xy);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/intersectors/
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

// Distance field functio for egg
float df(vec3 p) {
  // Add a bounce to it
  float ft = fract(TIME)-0.5;
  p.y += -(0.25-ft*ft);
  return egg(p, 3.0*vec3(1.0, 0.5, 0.))-0.25;
}

// Raymarcher function with a small tweak to make normals around edges less horrible
float rayMarch(vec3 ro, vec3 rd) {
  float t     = 0.0;
  vec2 nti = vec2(MAX_RAY_MARCHES,0);

  float i;
  for (i = 0.0; i < MAX_RAY_MARCHES; ++i) {
    if (t > MAX_RAY_LENGTH) break;
    float d = df(ro + rd*t);
    if (d<nti.x) nti = vec2(d,t);
    if (d < TOLERANCE) break;
    t += d;
  }

// This is the tweak
  if(i==MAX_RAY_MARCHES) t = nti.y;

  return t;
}


// Computes the normal of the egg by basically derivate the distance field
//  and normalize the derivate
vec3 normal(vec3 pos) {
  vec2 eps = vec2(NORM_OFF, 0.0);
  return normalize(vec3(
      df(pos+eps.xyy)-df(pos-eps.xyy)
    , df(pos+eps.yxy)-df(pos-eps.yxy)
    , df(pos+eps.yyx)-df(pos-eps.yyx))
    );
}

// Common way to cheat soft-shadows around a distance field.
float softShadow(vec3 ps, vec3 ld, float mint, float k) {

  float res = 1.0;
  float t = mint*6.0;
  for (float i=0.; i<MAX_SHADOW_MARCHES; ++i) {
    vec3 p = ps + ld*t;
    float d = df(p);
    res = min(res, k*d/t);
    if (res < TOLERANCE) break;
    t += max(d, mint);
  }
  return clamp(res, 0.0, 1.0);
}

// Computes the color given a ray origin and a ray direction
vec3 color(vec3 ro, vec3 rd) {
  const vec4 pdim = vec4(normalize(vec3(0.0, 1.0, 0.0)), 3.);
  float tp = rayPlane(ro, rd, pdim);

  float te = rayMarch(ro, rd);

  vec3 col = vec3(0.0);
  if (te < MAX_RAY_LENGTH && (tp < 0.0 || te < tp)) {
    // We hit the Egg
    vec3 ep = ro+rd*te;
    vec3 en = normal(ep);

    // Simple diffuse
    float sunDif = max(dot(en, SUNDIR), 0.0);
    // Fake diffuse from the sky, should sample the sky randomly and accumulate
    //  but that's expensive!
    float skyDif = max(dot(en, SKYDIR), 0.0);

    col += 0.1*GROUNDCOL+ sunDif+skyDif*sqrt(SKYCOL);
    col *= EGGCOL;
  } else if (tp > 0.) {
    // We hit the Ground
    vec3 gp = ro+rd*tp;
    vec3 gn = pdim.xyz;

    // Simple diffuse
    float sunDif = max(dot(gn, SUNDIR), 0.0);
    // Fake diffuse from the sky, should sample the sky randomly and accumulate
    //  but that's expensive!
    float skyDif = max(dot(gn, SKYDIR), 0.0);

    float sf = softShadow(gp, SUNDIR, 0.1, 8.0);
    col += 1.5*sf*sunDif+0.25*skyDif*sqrt(SKYCOL);
    col *= GROUNDCOL;

    // Fade out horizon, fine-tuned, no brain cells involved
    col *= tanh(40.0*(rd.y*rd.y)+0.125);
  } else {
    //  We hit the sky
    col += SKYCOL;

    // Make horizon more white, fine-tuned, no brain cells involved
    col += 0.1/max(sqrt(rd.y), 0.2);
  }

  col = sqrt(col);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 ro = vec3(0.0, 2.0, 10.0);
  ro.xz *= ROT(0.1*TIME);
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0, 1.0, 0.0);

  // Common way to set up orthagonal vectors that allows us to construct the ray direction
  //  from the the pixel pos
  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww ));
  vec3 vv = (cross(ww,uu));
  const float fov = tan(TAU/6.);
  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww);

  vec3 col = color(ro, rd);

  fragColor = vec4(col, 1.0);
}

