vec4 texture_linear_clamp(sampler2D _sampler, vec2 _P) {
    // texture() maps texels from ivec2(0) to ivec2(texture_size) to vec2(1.0 / texture_size) to vec2(1.0 - 1/texture_size)
    // this maps texels from ivec2(0)

    ivec2 _textureSize = textureSize(_sampler, 0).xy - ivec2(1, 1);

    _P *= vec2(_textureSize);

    vec2 _f = fract(_P);

    vec4 _texel00 = texelFetch(_sampler, clamp(ivec2(_P) + ivec2(0, 0), ivec2(0), _textureSize), 0);
    vec4 _texel10 = texelFetch(_sampler, clamp(ivec2(_P) + ivec2(1, 0), ivec2(0), _textureSize), 0);
    vec4 _texel01 = texelFetch(_sampler, clamp(ivec2(_P) + ivec2(0, 1), ivec2(0), _textureSize), 0);
    vec4 _texel11 = texelFetch(_sampler, clamp(ivec2(_P) + ivec2(1, 1), ivec2(0), _textureSize), 0);

    return mix(mix(_texel00, _texel10, _f.x), mix(_texel01, _texel11, _f.x), _f.y);
}

