    }

    if(iMouse.z > 0.1) fragColor.z += 0.2 * max(1.0 - 0.1 * length(iMouse.xy - fragCoord), 0.0);

    //fragColor.z *= 0.998+0.002*(fragCoord.y/iResolution.y);

