//spiral
void fx1( out vec4 O, in vec2 u )
{
    vec2 uv = u/iResolution.xy -.5;

    float l = length(uv)/3.;
    float a = atan(uv.y, uv.x);

    float b1 = sin(100.*(sqrt(l)-0.02*a-0.3*((iTime*50.+uv.y*(0.+cos(iTime*0.72)*150.9)+uv.x*(0.+cos(iTime*0.14)*100.9))/140.)))*2.54;
    float b2 = l*2.5;

    float g=max(b1/16.,0.09)+sin((a/2.5+iTime/2.5)*25.)*13.*(b2*b2);
    float b=sin((a/5.+iTime/3.5)*25.)*0.71*b2*b2*42.+0.62;

    vec3 col = vec3(b1*b1*b1*0.1+.62,
                    b1/3.5+b1*b*0.2-g/2.-.32,
                    b1/8.+b1*g*0.26+b/3.+.45);

    col = col*min(l*l*940000.,1.);
    float c1=max(col.x,max(col.y,col.z));
    c1=c1*c1*0.27;

    O = vec4(c1,c1,c1,1.0);
}

// plasma
void fx2( out vec4 O, in vec2 u )
{
    vec2  R = vec2(0.,iResolution.y+sin(iTime/25.0)*8.24),
          p = 5.*( u+u+vec2(800.,cos((iTime+5.)/10.0)*1000.) - R ) / R.y,
          r = p-p;

    for(float f=1.; f < 2.5; f *= 1.05 )	
		r += sin( p*f +iTime*0.85) / f,
        p = p * mat2(8,6,-8,6)*.1 + r*.4;

    float l = length(r);
    vec3 c = vec3(l*0.29, l*l*0.024, l*l*l*0.0016);

    vec2 uv = u/iResolution.xy;
    vec3 c2 = -0.3 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    O = vec4(c+c2, 0.);
