float transition(vec2 uv, int index, bool distinct) {

    float t = getTime(iTime, interval / speed);

    t = mod(t * speed, interval) - 0.25;
    t = clamp(t, 0., interval - 0.25);

    float a = length(uv) + 0.25;

    float t2 = t + tmod(index, uv);

    if (distinct) {
        if (a >= t2 - EDGE_SMOOTH && a < t2)
            t2 = smoothstep(0.,1.,(t2-a) / EDGE_SMOOTH);
        else
            t2 = step(a, t2); // a<n?1:0
    }
    else
        t2 = clamp(t2 - 0.25, 0., 1.);

    return t2;
}

void mainImage( out vec4 O, in vec2 u ) {

    vec4 px1, px2;
    fx1(px1, u);
    fx2(px2, u);

    int index = (int(iTime / (interval*(BOUNCE_TIME? 2.:1.) / speed))) % NOF_TRANSITIONS;

    if (!BOUNCE_TIME && (index & 1) == 1) { vec4 tmp=px1; px1=px2; px2=tmp; }

    vec2 uv = u/iResolution.y -.5;
    uv.x += (1. - iResolution.x/iResolution.y) * 0.5;

    float t = transition(uv, index, iMouse.z < 1. );

    O = mix(px1, px2, t);
}

