const int NOF_TRANSITIONS = 9;

const float EDGE_SMOOTH = 0.0; // 0.03

const bool BOUNCE_TIME = true;

float getTime(float time, float interval) {
    if (!BOUNCE_TIME)
        time = mod(time, interval);
    else {
        time = mod(time, interval*2.);
        if (time >= interval) time = max(interval*2. - time, 0.);
    }
    return time;
}

const float interval = 2.0;  // actual time of one transition: interval / speed
const float speed = 0.33;

float tmod(int index, vec2 uv) {
    switch(index) {

        case 0: return sin(((uv.x+0.53)/(uv.y+0.53)*200.+uv.y*100.)*0.2)*0.25;

        case 1: return sin(uv.x*200.)*0.125 + cos(uv.y*200.)*0.125;

        case 2: return sin(uv.x*200.*0.166)*0.125 + cos(uv.y*200.*0.25)*0.125;

        case 3: return sin((uv.x*100.*uv.y*100.)/3.)*0.15*cos((uv.y*200.*uv.y*200.)/5.)*1.5;

        case 4: return sin(uv.x*200.)*0.25;

        case 5: return sin((uv.x*200.+uv.y*100.)*0.1)*0.25;

        case 6: return sin((uv.x*200.-uv.y*300.)*0.35)*0.14+cos((uv.y*66.)*0.3)*0.09;

        case 7: return sin((uv.x*200.+uv.y*100.)*0.2)*0.06;

        case 8: return sin((uv.x*100.*uv.y*100.)*0.1)*0.25;

        default: return 0.;
    }
}

