float clock(float angle, vec2 uv1)
{
    if (max(abs(uv1.x), abs(uv1.y)) > 10.25) return 0.;

    vec2 hand = vec2(cos(angle), sin(angle));
    hand *= 1. / max(abs(hand.x), abs(hand.y)) * .25 / 21.;
    float alpha = 0.;
    for (float i = 1.5; i < 21.; i += 2.)
    {
        vec2 d = uv1 - hand*i;
        alpha += thetaY(1./180. - abs(d.x)) * thetaY(1./180. - abs(d.y));
    }

    return alpha;
}

