    float c = 10.0;
    float field = 0.0;
    vec3 color_field = vec3(0.0,0.0,0.0);

    for(float i = 0.0; i<METABLOBS; i = i + 1.0){
        float t = iTime * 0.03 * FF + (i)*1.0/(METABLOBS);
        float blob_field = 0.0;
        vec3 col = vec3(
            rand(i)*EXPOSURE,
            rand(i+0.1)*EXPOSURE,
            rand(i-0.1)*EXPOSURE);
        vec2 pos = combed_path(t, i,comb(iTime*0.05*FF+i,METABLOBS,0.3));
        for( float j = 0.0; j<SUBBLOBS; j = j + 1.0){
