}

float pillars(vec3 p) {
    float s = 12.;
    p = abs(p/s) - (vec3(2.,1.,2.5) * 0.5- 0.5);
    p = (p.x<p.z) ? p.zyx : p.xyz;
    p.z -= min(0.0, round(p.z));
    return sdCappedCylinder(p*s, 5., 1.);
}
float ceiling(vec3 p) {
    float plane1 = sdBox(p-vec3(0.,10.,0.), vec3(7.5,0.25,10.5));
    float plane2 = sdBox(p-vec3(0.,10.5,0.), vec3(7.,.5,10.));
    float plane3 = sdBox(p-vec3(0.,11.,0.), vec3(7.5,0.25,10.5));

    float cover = sdTriPrism2(p - vec3(0.,11.,0.),vec2(7.,0.),vec2(-7.,0.),vec2(0.,5.), 10.);
    vec3 tmpP = vec3(p.x,p.y,abs(p.z))/.8 - vec3(0.,11./0.8,13.);
    float detail = .8*sdTriPrism2(tmpP,vec2(7.,0.),vec2(-7.,0.),vec2(0.,5.), 1.);


    return min(min(plane1,min(plane2,plane3)),opSubtraction(detail,cover));
}

float buildingFloor(vec3 p) {
    float floorP = sdBox(p- vec3(0.,-0.5,0.), vec3(7.5,1.,10.5));

    float stairs1 = sdBox(p- vec3(0.,-0.5-0.75 / 2.,11.), vec3(2.5,0.75,.5));
    float stairs2 = sdBox(p- vec3(0.,-1. ,11.5), vec3(4.,0.5,1.5));
    float stairs3 = sdBox(p- vec3(0.,-1.25,12.), vec3(6.,0.25,3.));

    return min(min(stairs1,min(stairs2,stairs3)),floorP);
}

float ground(vec3 p) {
    return sdPlane(p- vec3(0.,-1.5,0.), vec3(0.,1.,0.), 0.);
}

float temple(vec3 p) {
    return min(pillars(p - vec3(0.,5.,0.)),min(buildingFloor(p),ceiling(p)));
}

vec2 scene(vec3 p) {
    float g = ground(p);
    // Bounding Sphere
    if(g > sdSphere(p,20.)) {
        float t = temple(p);
        float v = min(g, temple(p));
        return vec2(v, v == t? -1.:1.);
    }
    return vec2(g,1.);
}

vec3 getNormal(vec3 p) {
    float dx = scene(vec3(p.x + EPSILON, p.y, p.z)).x - scene(vec3(p.x - EPSILON, p.y, p.z)).x;
    float dy = scene(vec3(p.x, p.y + EPSILON, p.z)).x - scene(vec3(p.x, p.y - EPSILON, p.z)).x;
    float dz = scene(vec3(p.x, p.y, p.z + EPSILON)).x - scene(vec3(p.x, p.y, p.z - EPSILON)).x;

    return normalize(vec3(dx,dy,dz));
}


vec4 render(vec3 eye, vec3 dir, vec3 directionalLight, vec3 lightColor,vec3 ambient, vec3 groundColor, vec3 templeColor) {
    float dist = MIN_DIST;
    vec3 normDir = normalize(dir);

    for(int i = 0; i < MAX_MARCHING_STEPS; i++) {
        vec3 p = eye + normDir * dist;
        vec2 renderP = scene(p);
        float d = renderP.x;
        vec3 albedo = renderP.y < 0.? templeColor:groundColor;

        if(d < EPSILON) {
            vec3 normal = getNormal(p);
            float intensity = clamp(dot(directionalLight,-normal),0.,1.);
            if(intensity > 0.) {
                vec3 shadowDir = normalize( normal-directionalLight);
                float shadowDist = 2.*d + 2.*EPSILON;

                for(int is = 0; is < MAX_MARCHING_STEPS; is++) {
                    vec3 ps = p + shadowDir * shadowDist;

                    float ds = scene(ps).x;

                    if(ds < EPSILON) return vec4(albedo*ambient, 1.);

                    shadowDist +=ds;
                    if(shadowDist > MAX_DIST)
                        break;
                }
                return vec4(albedo*mix(ambient,lightColor, intensity ),1.);
            }

            return vec4(albedo*mix(ambient,lightColor, intensity ),1.);

        }

        dist += d;
        if(dist > MAX_DIST)
            break;
    }
    return vec4(.3,.5,.8,1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 eye = vec3(sin(iTime*0.5)*35., sin(3. +iTime*0.5)*15. + 25., cos(iTime*0.5)*60.);
    vec3 target = vec3(0., 5., 0.);
    vec3 directionalLight = normalize(vec3(1.,-1.5,-1.));
    vec3 ambientLight = vec3(0.5,0.5,0.6);
    vec3 dirLightColor = vec3(0.8,0.8,0.6);
    vec3 groundColor = vec3(0.2,0.6,0.3);
    vec3 templeColor = vec3(0.25,0.25,0.15);

    mat4 viewToWorld = generateViewMatrix(eye, target, vec3(0., 1., 0.));

    vec4 color = vec4(0.);
    int AA =2;
    for(int i = 0; i < AA; i++)
        for(int j = 0; j < AA; j++) {
            vec2 offset = vec2(float(i),float(j)) / float(AA) - 0.5;
            vec3 rayDir = rayDirection(60., iResolution.xy+ offset, fragCoord);
            vec3 worldRayDir = (viewToWorld * vec4(rayDir, 0.)).xyz;
            color += vec4(pow(render(
                    eye, 
                    worldRayDir, 
                    directionalLight, 
                    dirLightColor, 
                    ambientLight,
                    groundColor,
                    templeColor

                ).xyz,vec3(0.4545)),1.);
        }

    fragColor = color/ float(AA*AA);
}

