#define pi 3.14159
#define dir(a) vec2(cos(a),sin(a))
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = 2.*(U+U-R)/R.y,
         I, // Integer part
         F; // Fractional part

    float n = 3., // Subdivision number
          o = .2 + .3 * smoothstep(2., 4., iTime); // Offset from centre

    O *= 0.;

    for (int i;  i++ < 2; ) 
    for (int j;  j++ < 2; ) {   
        I = ceil(u + 1. - vec2(i, j)),  
        F = I - u;

        //O += smoothstep(-4./R.y, 4./R.y, .02 - abs(.42- length(F))); 

        // Rotate in checkerboard pattern,
        // offset rotation by pi every 2nd row
        float a = atan(F.y, F.x)
                + (mod(I.x+I.y, 2.) - .5) * iTime
                - pi * mod(I.y, 2.);

        // Subdivide angle
        a = mod(a, 2.*pi/n);

        // Centre F in each subdivision
        F = length(F) * dir(a) - o * dir(pi/n);

        // Draw n circles, one in each subdivision
        O += smoothstep(-4./R.y, 4./R.y, .02 - abs(.07- length(F)));
     }
}

