// 16x acceleration of https://www.shadertoy.com/view/4tSyzy
// by applying gaussian at intermediate MIPmap level.

const int samples = 35,
          LOD = 2,         // gaussian done on MIPmap at scale LOD
          sLOD = 1 << LOD; // tile size = 2^LOD
const float sigma = float(samples) * .25;

float gaussian(vec2 i) {
    return exp( -.5* dot(i/=sigma,i) ) / ( 6.28 * sigma*sigma );
}

vec4 blur(sampler2D sp, vec2 U, vec2 scale) {
    vec4 O = vec4(0);  
    int s = samples/sLOD;

    for ( int i = 0; i < s*s; i++ ) {
        vec2 d = vec2(i%s, i/s)*float(sLOD) - float(samples)/2.;
        O += gaussian(d) * textureLod( sp, U + scale * d , float(LOD) );
    }

    return O / O.a;
}

vec4 threshold(in vec4 tx){
    const float Soft = 0.001;
    const float Threshold = 0.02;
    float f = Soft / 5.0;
	float a = Threshold - f;
	float b = Threshold + f;
    float l = (tx.x + tx.y + tx.z) / 3.0;

	float v = smoothstep(a, b, l);

	return vec4(v);

}

void mainImage(out vec4 Result, vec2 U) {
    vec4 blurred = blur( iChannel0, U/iResolution.xy, 1./iChannelResolution[0].xy);
    vec4 subbed = texture(iChannel0, U/iResolution.xy) - blurred;
    Result = threshold(subbed);

}

