vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract(.5+(a1)*(a2.x+a2.y))-.5)
    ;
}

vec3 fractal(vec2 uv,vec2 t2){
    vec3 col = vec3(0.);
    bool b = true;
    for(int k = 0; k < 6; k++){
        uv =
            abs(.5+uv+t2)/1.5
        ;
        //uv = abs(fract(vec2(uv+vec2(.5,1.5))*3.)-.5)/3.;
        b =
            uv.y>uv.x
            //uv.y>uv.x||!b
        ;
        if(
            b
        ) uv = uv.yx;
        t2 =
            -triangle_wave(uv-.5).yx
        ;
        uv =
            (t2.yx-triangle_wave(uv.yx+1.)).yx
        ;
        //if(uv.y<uv.x||b)
        col =
            vec3(col.zx,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.))
            //vec3(col.zx,max((uv.x+uv.y+1.),0.))
            //abs(vec3(col.zx,max(uv.y-uv.x,col.y)))
            //vec3(col.zx,max(col.y,max(uv.y-uv.x,0.)))
            //vec3(col.zx,max(col.y,max(abs(uv.x-uv.y)*sign(t2.x-t2.y),0.)))
        ;

        }
        return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = vec4(0.0);
    float t1 = 1.;
    vec2 draggedPos = texelFetch(iChannel0, ivec2(1,0), 0).xy;

    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    uv -= draggedPos/iResolution.y/t1/2.0;
    vec2 t2 = vec2(0.);
    vec3 col = fractal(uv,t2);
    for(int i = 0; i < 1; i++){
        col = abs(fractal(uv,t2));
    }

    fragColor = vec4(col*2.,1.0);
}

