float fmod(float a, float b){
    //a /= 1. + mod(1.+b/2.,2.);
    return
        mod(mod(floor(a),b)*mod(floor((a)/b),b),b)
        //mod(mod(floor(a),b+mod(floor(a/8.),b)),b)
        //floor(mod(mod(floor(a)/b,b)*mod(floor((a)/b),b),b))
        //floor(mod(mod(floor(a),b*2.)*mod(floor((a/2.)/b),b),b))
        //mod(mod(floor(a),b)*mod(floor((a)/b),b),b*b)
        //mod(mod(floor(a),b)*mod(floor((a)/8.),b),b)
    ;
}

float prime_rhythm(float time){
    //divide by prime numbers to get an interesting rhythm
    return (1.+fmod(time/2.,2.))*(1.+fmod(time/3.,2.))*(1.+fmod(time/5.,2.))*(1.+fmod(time/7.,2.));
}

vec2 mainSound(int samp, float time){
  float s1 =
      8.,
  tempo1 =
      prime_rhythm(time)
  ,
  t = s1*time/tempo1,
  t4 =
      //t/tempo1
      t*tempo1
  ,
  t1 = fmod(t4+1.,2.),
  t5 = t,
  t3 = (1.+fmod(t5,2.)),
  t6 = time/t3,
  m3 =
      floor(fmod(t6,s1/2.)/2.),
      //floor(fmod(t6,s1/2.+floor(tempo1/2.))/2.),
  m2 =
      1. + fmod(t/t3/2.,s1/2.)
      //1. + fmod(t/t3/2.,s1/2.)
  ;
  vec2 a= vec2(
      //(1.-log(fract(t/tempo1)/2.))*(1.-log(fract(t/prime_rhythm(time+7.))/4.))
      pow(1.-log(fract(t/tempo1)/2.),2.),pow(2.,1.-log(fract(t/prime_rhythm(time+7.))/4.))
  ),
  nb = vec2(
      t*pow(2.,(m3+m2-t1*4.)/7.+7.5)*tempo1/8.
  );
  return
      abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a
      //log(a/8.-abs(a/16.-abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a))
      //log(a/8.-abs(a/4.-abs(.5-vec2(fract(nb.x*.998),fract(nb.y/2.)))*a))
      //abs(a/16.-abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a)

      //sitar
      //log(abs(a/16.-abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a))

      //log(a/16.-abs(a/4.-abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a))


      //abs(.5-vec2(fract(t*nb*.998/prime_rhythm(time+14.)),fract(t*nb/prime_rhythm(time+7.)/2.)))*a
  ;
}

